COMPONENT.script:
initialize()=
declare
	rc					= integer not null;
	HideInfoPanel		= procedure returning integer not null;
	ShowInfoPanel		= procedure returning integer not null;
	StartInfoPanel		= procedure returning integer not null;
enddeclare


/*
**@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@
**			RUNTIME (EXEC) PROCEDURES
**@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@
**
**	These are called from the event-response 3GL handlers at runtime
*/

/*
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**  Position, size and display the info panel frame
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
procedure ShowInfoPanel(

	frame		= FrameExec default null;
	hostfield	= FormField default null;

)=
declare
	child			= FrameExec default null;
	ipsource		= FrameSource default null;
	status			= integer not null;
	widgetid		= integer not null;
enddeclare
{
	if frame is null or hostfield is null then
		message 'ShowInfoPanel was called but either frame or hostfield parameter was not provided.';
		return ER_FAIL;
	endif;

	/*
	**  Access the infopanel frame source
	**
	**  This implementation stores a single instance of the panel at a
	**  common location (the panel's framesource). An alternative is to
	**  store frame-local instances instead.
	*/
	ipsource = curprocedure.ObjectSource.ParentApplication.FetchComponent(
		componentname='infopanel');
	ipsource.FetchTaggedValue(tag='instance', item=Byref(child));

	/*
	**  Start the infopanel from here ..if it isnt already running
	**
	**  If we do this, we must return now, as the frame will not actually
	**  start until this 4GL procedure completes (frame calls must be
	**  asynchronous) - so there will be no popup until ShowInfoPanel is
	**  triggered again
	*/
	if child is not null then
		widgetid = child.WidgetId;
	endif;

	if child is null or widgetid = 0 then

		/*
		**  Call the Asynchronizer;
		**  Return .. unless we were called from the asynchronizer ghost
		*/
		status = IE.QueueResponse(frame=frame, hostfield=hostfield);

		if status != ER_OK then
			return status;
		endif;

		/*
		**  Start the panel and get the reference to it
		**  (to get here, we must have been called by the ghost)
		*/
		status = StartInfoPanel(frame=frame, hostfield=hostfield);
		ipsource.FetchTaggedValue(tag='instance', item=Byref(child));
	endif;

	/*
	**  Set the info to display
	*/
	child.FieldByName('title').SetAttribute(
		stringvalue=hostfield.GetTagValue(tag='title').Duplicate());
	child.FieldByName('description').SetAttribute(
		stringvalue=hostfield.GetTagValue(tag='description').Duplicate());
	FormField(child.FieldByName('title')).ParentField.UpdBackground(opacity=0.80);

	/*
	**  Position, size and display the info panel frame
	*/
	child.SetAttribute(
		WindowWidth=1, WindowHeight=1);

	child.SetAttribute(
		windowxleft=frame.WindowXLeft+hostfield.AbsXRight,
		windowytop=frame.WindowYTop+hostfield.AbsYBottom + 229,
		isautosized=TRUE);

	return ER_OK;
}


/*
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**  Hide the info panel frame
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
procedure HideInfoPanel(

	frame		= FrameExec default null;
	hostfield	= FormField default null;

)=
declare
	child			= FrameExec default null;
	ipsource		= FrameSource default null;
enddeclare
{
	/*
	**  Access the infopanel frame;
	**  Make it invisibly small
	*/
	ipsource = curprocedure.ObjectSource.ParentApplication.FetchComponent(
		componentname='infopanel');
	ipsource.FetchTaggedValue(tag='instance', item=Byref(child));

	child.SetAttribute(WindowWidth=1, WindowHeight=1);

	return ER_OK;	
}


/*
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**  Start the info panel frame
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
procedure StartInfoPanel(

	frame		= FrameExec default null;
	hostfield	= FormField default null;

)=
declare
	child			= FrameExec default null;
	ipsource		= FrameSource default null;
	tagv			= TaggedValue default null;
	status			= integer not null default ER_OK;
enddeclare
{
	/*
	**  Open the infopanel for general availability;
	**  Start tiny so it cannot be seen until needed
	*/
	child = openframe InfoPanel() with
		parentframe=frame,
		windowwidth=10, windowheight=10,
		istopmost=TRUE;

	/*
	**  Store the infopanel reference where it can always be found
	**  (make sure the taggedvalue is not saved)
	*/
	ipsource = frame.ObjectSource.ParentApplication.FetchComponent(
		componentname='infopanel');
	ipsource.SetTaggedValue(tag='instance', item=child);

	tagv = ipsource.FetchTaggedValue(tag='instance');
	tagv.Availability = TVA_TEMPORARY;

	return ER_OK;	
}
