COMPONENT.script:
/*
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
INITIALIZE =
  DECLARE
  Split			= PROCEDURE RETURNING ARRAY OF StringObject;
  ENDDECLARE


/*
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**	Revert to the original demodb contents
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
ON CLICK RevertBtn =
  DECLARE
  ct			= integer NOT NULL;
  existing_columns	= ARRAY OF StringObject;
  coldef		= ChoiceDetail DEFAULT NULL;
  name			= varchar(32) NOT NULL;
  query			= varchar(2000) NOT NULL;
  ENDDECLARE
BEGIN
SELECT count(*) AS ct FROM iitables WHERE table_name = 'flight';

IF ct > 0 THEN
  DROP TABLE flight;
ENDIF;

DELETE FROM user_profile WHERE up_id > 200;

ct = 1; existing_columns.Clear();
SELECT DISTINCT column_name AS existing_columns[ct].Value
 FROM iicolumns 
 WHERE table_name = 'user_profile'
 BEGIN
 ct = ct + 1;
 END;

FOR ct = 1 TO FIELD(user_profile_columnlist).ValueList.ChoiceItems.LastRow DO
  coldef = FIELD(user_profile_columnlist).ValueList.ChoiceItems[ct];
  name = coldef.EnumText;
  IF existing_columns.Find('value', value=name) != ER_OK THEN CONTINUE; ENDIF;    

  query = 'ALTER TABLE user_profile DROP COLUMN ' + :name  + ' RESTRICT';
  EXECUTE IMMEDIATE :query;
ENDFOR;

COMMIT;
END;


/*
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**	Extend the demodb database to have more realistic data and relationships
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
ON CLICK ExtendDemodbBtn =
  DECLARE
  dba			= varchar(32) NOT NULL;
  dbadmin		= varchar(32) NOT NULL;
  username		= varchar(32) NOT NULL;
  name			= varchar(32) NOT NULL;
  datatype		= varchar(32) NOT NULL;
  ct			= integer NOT NULL;
  application		= AppSource DEFAULT NULL;
  coldef		= ChoiceDetail DEFAULT NULL;
  user_profile_columns	= ClassSource DEFAULT NULL;
  flight_columns	= ClassSource DEFAULT NULL;
  existing_columns	= ARRAY OF StringObject;
  existing_tables	= ARRAY OF StringObject;
  query			= varchar(2000) NOT NULL;
  tokens		= ARRAY OF StringObject;
  addresses		= ARRAY OF ChoiceDetail;
  flights		= ARRAY OF ChoiceDetail;
  id			= integer NOT NULL;
  addeduser_indx	= integer NOT NULL;
  fk_list		= ChoiceList;

  image			= LongByteObject default null;
  store			= CompSource default null;
  store#		= StringHashTable default null;
  itemlist		= ChoiceList default null;
  cvr			= LongItemStore;
  i			= integer not null;
  rc			= integer not null;
  indx			= integer not null;
  citem			= ChoiceDetail default null;
  searchstring		= varchar(256) not null;
  ENDDECLARE
BEGIN
/*
**  Reference key items
*/
addresses = FIELD(addresslist).ValueList.ChoiceItems;

application = CurFrame.ObjectSource.ParentApplication;

/*
**  Clear the decks
*/
ROLLBACK;

/*
**  Ensure we are connected to the demodb database
*/
IF CurFrame.DbSession.Database NOT LIKE '%demo%' THEN
  MESSAGE 'ERROR: CurFrame.DbSession.Database NOT LIKE %demo%';
  RETURN ER_FAIL;
ENDIF;

/*
**  Ensure we have sufficient privilege to proceed
*/
SELECT dbmsinfo('dba') AS dba, dbmsinfo('username') AS username,
	dbmsinfo('db_admin') AS dbadmin;
IF dba = username THEN 
ELSEIF dbadmin = 'Y' THEN
  SET SESSION AUTHORIZATION :dba;
ELSE
  MESSAGE 'ERROR: Do not have sufficient privilege to proceed' ;
  RETURN ER_FAIL;
ENDIF;

/*
**  Extend the user_profile table
*/
ct = 1; existing_columns.Clear();
SELECT DISTINCT column_name AS existing_columns[ct].Value
 FROM iicolumns 
 WHERE table_name = 'user_profile'
 BEGIN
 ct = ct + 1;
 END;

FOR ct = 1 TO FIELD(user_profile_columnlist).ValueList.ChoiceItems.LastRow DO
  coldef = FIELD(user_profile_columnlist).ValueList.ChoiceItems[ct];
  name = coldef.EnumText; datatype = coldef.EnumDisplay;
  IF existing_columns.Find('value', value=name) = ER_OK THEN CONTINUE; ENDIF;    

  query = 'ALTER TABLE user_profile ADD COLUMN ' + :name  + ' ' + :datatype + 
	' WITH DEFAULT';
  EXECUTE IMMEDIATE :query;
ENDFOR;

/*
**  Get any images stored in this frame that are to be used as data
*/
store = curframe.ObjectSource;
itemlist = cvr.ListStoredItems(store=store, type='images');
store# = itemlist.ClientData;

/*
**  Transfer them to the corresponding records in the addresses choicelist
*/
for i = 1 to itemlist.ChoiceItems.LastRow do
  citem = itemlist.ChoiceItems[i].Duplicate();

  if citem.EnumText like '%nigh%' then
    searchstring = '%nigh%';
  elseif citem.EnumText like '%poole%' then
    searchstring = '%poole%';
  elseif citem.EnumText like '%damore%' then
    searchstring = '%damore%';
  endif;

  rc = addresses.Find(attributename='enumtext',
    searchstring=searchstring, rownumber=Byref(indx));

  if rc != ER_OK then
    continue;
  endif;

  addresses[indx].ClientData = store#.Find(key=citem.EnumText);
endfor;

/*
**  Add user records (basic information only) to the user_profile table;
**  Capture the key data for use in the flight table data
*/
FOR ct = 1 TO addresses.LastRow DO
  tokens = Split(text=addresses[ct].EnumText, delimiter='|');
  id = addresses[ct].EnumValue;
  image = addresses[ct].ClientData;

  INSERT INTO user_profile 
	(up_id,up_last,up_first,up_email,up_airport)
  VALUES
	(id,tokens[1].Value,tokens[2].Value,tokens[3].Value,tokens[4].Value);

  if image is not null then
    image.UpdateInDb(query='update user_profile set up_image where up_id=' + varchar(:id));
  endif;

  fk_list.ChoiceItems[ct].EnumValue = id; 
  fk_list.ChoiceItems[ct].EnumText = tokens[3].Value; 
ENDFOR;

/*
**  Add address data to the user_profile table
*/
FOR ct = 1 TO addresses.LastRow DO
  tokens = Split(text=addresses[ct].EnumDisplay, delimiter='|');
  id = addresses[ct].EnumValue;

  UPDATE user_profile
   SET 	up_street = :tokens[1].Value,
	up_town_or_city = :tokens[2].Value,
	up_state_or_country = :tokens[3].Value,
	up_postal_code = :tokens[4].Value,
	up_telephone = :tokens[5].Value,
	up_mobile = :tokens[6].Value
   WHERE up_id = :id;

ENDFOR;

								/////commit;
/*
**  Create the flight table ..if it hasn't yet been created
*/
ct = 1;
SELECT DISTINCT table_name AS existing_tables[ct].Value
 FROM iitables 
 WHERE table_owner != '$ingres'
 BEGIN
 ct = ct + 1;
 END;

IF existing_tables.Find('value', value='flight') = ER_OK THEN 
ELSE

  /*
  **  Create the flight table
  */
  query = 'CREATE TABLE flight (';

  FOR ct = 1 TO FIELD(flight_columnlist).ValueList.ChoiceItems.LastRow DO
    coldef = FIELD(flight_columnlist).ValueList.ChoiceItems[ct];
    name = coldef.EnumText; datatype = coldef.EnumDisplay;
    query = query + HC_NEWLINE + :name  + ' ' + :datatype + ',';
  ENDFOR;

  query = query + HC_NEWLINE + 'primary key(fl_id),'; 
  query = query + HC_NEWLINE + 'foreign key(fl_email) references user_profile,'; 
  query = query + HC_NEWLINE + 'foreign key(fl_depart_from) references airport(ap_iatacode),'; 
  query = query + HC_NEWLINE + 'foreign key(fl_arrive_to) references airport(ap_iatacode),'; 
  query = Left(query,Length(query)-1);

  query = query + HC_NEWLINE + ')';

  EXECUTE IMMEDIATE :query;

  query = 'MODIFY flight TO btree ON fl_user,fl_arrive_to,fl_depart_at';

  EXECUTE IMMEDIATE :query;

  /*
  **  Add trip data to the flight table
  */
  flights = FIELD(flightlist).ValueList.ChoiceItems;
  FOR ct = 1 TO flights.LastRow DO
    tokens = Split(text=flights[ct].EnumDisplay, delimiter='|');

    addeduser_indx = Int4(tokens[1].Value);
    tokens[1].Value = varchar(fk_list.ChoiceItems[addeduser_indx].EnumValue);
    tokens[2].Value = varchar(fk_list.ChoiceItems[addeduser_indx].EnumText);

    INSERT INTO flight
	(fl_user,fl_email,fl_trip,fl_depart_at,fl_id,fl_airline,fl_flight_num,fl_depart_from,fl_arrive_to,fl_arrive_at)
    VALUES
	(tokens[1].Value,tokens[2].Value,
	 tokens[3].Value,tokens[4].Value,tokens[5].Value,tokens[6].Value,
	 tokens[7].Value,tokens[8].Value,tokens[9].Value,tokens[10].Value);
  ENDFOR;

ENDIF;

/*
**  Ensure privilege is sufficient for our purposes, whoever we turn out to be
*/
GRANT ALL ON AIRLINE TO PUBLIC;
GRANT ALL ON AIRPORT TO PUBLIC;
GRANT ALL ON COUNTRY TO PUBLIC;
GRANT ALL ON FLIGHT TO PUBLIC;
GRANT ALL ON FLIGHT_DAY TO PUBLIC;
GRANT ALL ON FULL_ROUTE TO PUBLIC;
GRANT ALL ON ROUTE TO PUBLIC;
GRANT ALL ON TZ TO PUBLIC;
GRANT ALL ON USER_PROFILE TO PUBLIC;
GRANT ALL ON VERSION TO PUBLIC;

/*
**  Commit
*/
COMMIT;

END;



//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
//	Split a delimited string into an array of tokens
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
PROCEDURE Split (

  text		= varchar(2000) NOT NULL;
  delimiter	= varchar(10) NOT NULL DEFAULT ' ';
  rows 		= integer NOT NULL;

  )=
  DECLARE
  tokens	= ARRAY OF StringObject;
  loc 		= integer NOT NULL;
  ct 		= integer NOT NULL;
  token 	= varchar(256) NOT NULL;
  ENDDECLARE
BEGIN
WHILE text != '' OR loc < Size(text) DO
  loc = Locate(text,delimiter);
  token = Left(text,loc-1);
  WHILE token LIKE ' %' DO token = Shift(token,-1); ENDWHILE; //remove leading blanks
  tokens[tokens.LastRow+1].Value = token;
  text = Shift(text,-loc);
ENDWHILE;

IF rows > 0 THEN
  FOR ct = tokens.LastRow+1 TO rows DO tokens.InsertRow(ct); ENDFOR;
  FOR ct = tokens.LastRow DOWNTO rows+1 DO tokens.RemoveRow(ct); ENDFOR;
ENDIF;

RETURN tokens;
END;

