#define $BIGNUM		'100000000'

initialize =
declare
	fd$					= varchar(32) not null default '\';
	rc					= integer not null;

	CallingFrame	= procedure returning FrameExec;
enddeclare


//@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@
//							FILL METHODS
//@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@

/*
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**	Prepare to apply a set of fill coordinates as a delayed userevent sequence 
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
method PrepareFill (

	curframe	= FrameExec default null;
	fld			= FormField default null;
	pattern		= varchar(32) not null default 'none';
	side		= integer not null;
	width		= integer not null;
	height		= integer not null;
	duration	= integer not null default 1;
	granularity	= integer not null default 1;
	delay		= float4 not null;		//byref

)=
declare
	string		= StringObject default null;
	methodname	= varchar(32) not null;
	patterns	= varchar(256) not null default
		' nwse nesw spiral ';
enddeclare
{
	if curframe is null then
		curframe = CallingFrame();
	endif;

	if patterns not like Concat('% ' + pattern + ' %','') then
		pattern = 'none';
	endif;

	if pattern = 'none' then
		return null;
	endif;

	if side = 0 then
		return null;
	endif;

	methodname = pattern + 'fill';
	string = CurObject.:methodname(side=side, width=width, height=height);
	delay = float4(duration)/float4(granularity);
	return string;
}


/*
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**	Replace an area of bitmap applying a set of fill coordinates
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
method Fill (

	string		= StringObject default null;
	fld		= FormField default null;
	image		= BitmapObject default null;
	startpos	= integer not null;
	chunksize	= integer not null;

)=
declare
	endpos		= integer not null;
	chunk		= StringObject default null;
	bitmap		= BitmapObject default null;
enddeclare
{
	if startpos = 0 then
		startpos = 1;
	endif;

	chunk = string.ExtractString(startpos,chunksize);
	endpos = chunksize;
	while chunk.SubString(endpos,1) != ' ' do
		endpos = endpos - 1;
	endwhile;
	chunk = chunk.ExtractString(1,endpos);
	startpos = startpos+endpos;

	/*
	**	Display the pixels
	*/
	bitmap = fld.BgBitmap.ReplaceBitmap(
		bitmap=image,
		asnewobject=TRUE, coordlist=chunk);
	if bitmap is not null then
		fld.BgBitmap = bitmap;
		fld.UpdBackground();
	endif;

	return startpos;
}


/*
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**	Define the coordinates for an angled fill
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
method	nwsefill(

	side		= integer not null;
	width		= integer not null;
	height		= integer not null;

)=
{
	return curobject.AngledFill(
		side=side, width=width, height=height, type='nwse');
}


method	neswfill(

	side		= integer not null;
	width		= integer not null;
	height		= integer not null;

)=
{
	return curobject.AngledFill(
		side=side, width=width, height=height, type='nesw');
}



method	angledfill(

	type		= varchar(32) not null default 'nwse';
	side		= integer not null;
	width		= integer not null;
	height		= integer not null;

)=
declare
	signx			= integer not null default 1;
	startx			= integer not null default 0;

	string			= StringObject;
	i				= integer not null default 0;
	j				= integer not null;
	k				= integer not null;
	seg				= integer not null;
	x				= integer not null;
	y				= integer not null;
	strings			= array of StringObject;
	resultstring	= StringObject;
	ct				= integer not null;
	frags			= integer not null default 10;
enddeclare
{
	if width = 0 then
		width = side;
	endif;
	if height = 0 then
		height = side;
	endif;

	if type = 'nwse' then
		startx = 0;
		signx = 1;
	elseif type = 'nesw' then
		startx = width;
		signx = -1;
	endif;

seg = side/frags;
for k = 1 to frags+1 do strings.InsertRow(); endfor;
for k = 1 to frags+1 do
	string = strings[k];
	for i = i+1 to k*seg do
		if i > side then endloop; endif;

		for j = 1 to i do
			ct = ct + 1;
			x = startx + signx*(i-j+1);
			y = j;
			if x <= 0 or x > width or y <= 0 or y > height then
				continue;
			endif;
			string.ConcatVarchar(varchar(x) + ',' + varchar(y) + ' ');
		endfor;
	endfor;
	i = i - 1;
endfor;

resultstring.Join(strings);

return resultstring;
}


/*
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**	Define the coordinates for a spiral fill
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
method spiralfill(

	side		= integer not null;
	infl		= float not null default 1.20;

)=
declare
	data		= array of $_ComponentName;
	datum		= $_ComponentName default null;
	rcur		= float4 not null;
	rcurs		= array of FloatObject;
	string		= StringObject;
	xcenter		= integer not null;
	ycenter		= integer not null;
	i		= integer not null;
	j		= integer not null;
	x		= integer not null;
	y		= integer not null;
	tan		= float4 not null default 0;
enddeclare
{
	xcenter = side/2;
	ycenter = side/2;
	i = 1;
	rcur = 1;
	while rcur < sqrt(xcenter**2+ycenter**2) do
		rcurs[rcurs.LastRow+1].Value = rcur;
		rcur = rcur * infl;
	endwhile;

	for x = 1 to side do
		for y = 1 to side do
			if x = xcenter and y = ycenter then continue; endif;
			data.InsertRow(rownumber=i);
			datum = data[i];
			datum.x = x-xcenter;
			datum.y = y-ycenter;
			datum.r = sqrt(datum.x**2+datum.y**2);

			for j = 1 to rcurs.LastRow do
				if datum.r >= rcurs[j].Value then continue; endif;
				rcur = rcurs[j-1].Value;
				endloop;
			endfor;

			datum.rsub = datum.r - rcur;

			if datum.y != 0 then
				tan = float4(datum.x)/float4(datum.y);
			else
				tan = $BIGNUM;
			endif;

			datum.theta = atan(tan);
			i = i + 1;
		endfor;
	endfor;

	data.Sort(r=AS_ASC, theta=AS_ASC, rsub=AS_ASC);

	for i = 1 to data.LastRow do
		string.ConcatVarchar(varchar(data[i].x+xcenter) + ',' + varchar(data[i].y+ycenter) + ' ');
	endfor;

	return string;	
}


//@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@
//							LONGITEM STORE METHODS
//@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@

/*
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**	Prepare and add an item to the store
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
method StoreLongItem(

	store			= SourceObject default null;
	longitem		= LongByteObject default null;
	itemname		= varchar(256) not null;
	type			= varchar(32) not null default 'images';

)=
declare
	indx			= integer not null;
	list			= ChoiceList default null;
	i				= integer not null;
	replace			= integer not null default FALSE;
enddeclare
{
	if store is null or longitem is null then
		return PU_NONE;
	endif;

	/*
	**	Access the item store (create it if necessary)
	*/
	indx = curobject.OpenItemStore(store=store, type=type);
	list = store.Extension[indx];

	/*
	**	Ensure there is a unique keyname for this image
	*/
	if itemname = '' then
		itemname = curobject.NameFromFilehandle(filehandle=longitem.FileHandle);		
	endif;

	for i = 1 to 2 do
		if itemname != '' and list.IndexByText(textvalue=itemname) != 0 then
			rc = curmethod.ConfirmPopup(
				messagetext='This will replace the existing item called ' + itemname +
				'. Click OK to proceed, Cancel to leave the item in place');
			if rc = PU_OK then
				replace = TRUE;
				endloop;
			endif;

			itemname = '';
		endif;

		if i = 2 then
			endloop;
		endif;

		if itemname = '' then
				itemname = prompt 'Please provide a name for this image (leave blank to cancel)';
			if itemname = '' then
				return PU_CANCEL;
			endif;
		endif;
	endfor;


	/*
	**	Add the item to the store
	*/
	rc = curobject.AddItemToStore(
		storelist=list, itemname=itemname, longitem=longitem, replace=replace);

	return rc;
}


/*
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**	Add a prepared item to the store
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
method AddItemToStore(

	storelist		= ChoiceList default null;
	itemname		= varchar(256) not null;
	longitem		= LongByteObject default null;
	replace			= integer not null default FALSE;

)=
declare
	indx			= integer not null default 0;
	store#			= StringHashTable default null;
	bit64			= StringObject;
enddeclare
{
	store# = storelist.ClientData;
	rc = store#.InsertObject(key=itemname, object=longitem);
	if rc = ER_OK then
		if replace = TRUE then
			indx = storelist.IndexByText(textvalue=itemname);
		endif;
		if indx = 0 then
			indx = storelist.AddItem(
				enumvalue=storelist.ChoiceItems.LastRow+1,
				textvalue=itemname,
				displayvalue=itemname,
				bitmapvalue=NULL);
		endif;
		rc = bit64.ConvertFromBinary(binary=longitem, format=EF_BASE64);
		if rc = ER_OK then
			storelist.ChoiceItems[indx].EnumSubText[1] = bit64;
		endif;
	endif;

	return indx;
}


/*
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**	Get the choicelist of stored items, with the items attached
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
method ListStoredItems(

	store	= SourceObject default null;
	type	= varchar(32) not null default 'images';

)=
declare
	list	= ChoiceList;
	indx	= integer not null;
enddeclare
{
	/*
	**	Access the index of entries in the imagestore
	**	(this also ensures it is up to date)
	*/
	indx = curobject.OpenItemStore(store=store, type=type);
	list = store.Extension[indx];

	/*
	**	Return list and store (as the list's clientdata)
	*/
	return list;
}


/*
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**	Open a store of objects
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**
**	Objects like longbyte objects must be converted to base64 and stored as
**	string objects.
**	Note that the hash key is varchar, converted from the EnumText nvarchar. This
**	is important: if the key used for insert is an nvarchar, but the key
**	subsequently used for a find is varchar, it fails, but only the first
**	time the store is accessed.
*/
method OpenItemStore (

	store		= SourceObject default null;
	type		= varchar(32) not null default 'images';

)=
declare
	indx		= integer not null;
	itemlist	= ChoiceList;
	extlist		= ChoiceList default null;
	citems		= array of ChoiceDetail default null;
	store#		= StringHashTable;
	indexkey	= varchar(32) not null;
	i			= integer not null;
	bit64		= StringObject default null;
	longitem	= LongByteObject default null;
	name		= varchar(256) not null;
enddeclare

{
	indexkey = 'storeindex_' + type;

	/*
	**	Return the store ..if it already exists
	**	(may need to repopulate it first, if first use of the session,
	**	since hashtable contents are not saved (the table itself is ...)
	*/
	indx = Int4(store.GetTagText(tag=indexkey));

	if indx != 0 and indx <= store.Extension.LastRow then

		/* switch to a fresh choicelist for the extension
		**   (probably only need this when repopulating)
		*/
		extlist = store.Extension[indx];	//raise the usecount
		itemlist.ClientData = extList.ClientData;
		citems = extlist.ChoiceItems;
		for i = 1 to citems.LastRow do
			itemlist.ChoiceItems[i] = citems[i];
		endfor;
		store.Extension[indx] = itemlist;

		if itemlist.ClientData is null then
			itemlist.ClientData = store#;
		endif;

		store# = itemlist.ClientData;

		/*
		**	Repopulate the store if necessary
		*/
		if store#.ObjectCount = 0 then
			/*
			**	Insert a dummy first entry - find sometimes misses entry number 1
			*/
			citems = itemlist.ChoiceItems;
			for i = 1 to citems.LastRow do
				longitem = LongByteObject.Create();
				if citems[i].EnumSubText.LastRow = 1 then
					bit64 = citems[i].EnumSubText[1];
					rc = longitem.ConvertFromString(string=bit64, format=EF_BASE64);
				endif;
				if rc = ER_OK then
					name = citems[i].EnumText;
					rc = store#.InsertObject(key=name, object=longitem);
				endif;
			endfor;
		endif;

		return indx;
	endif;

	/*
	**	Create the store ..otherwise (if it doesnt already exist)
	*/
	indx = store.Extension.LastRow+1;
	store.Extension[indx] = itemlist;
	itemlist.ClientData = store#;
	store.SetTaggedValue(tag=indexkey, textvalue = varchar(indx));

	store.Extension[indx] = itemlist;

	return indx;
}


/*
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**	Load a longbyteobject-stored image into a bitmap object
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
method LoadLongImage(

	bitmap		= BitmapObject default null;
	list		= ChoiceList default null;
	name		= varchar(256) not null;

)=
declare
	store#		= StringHashTable default null;
	longimage	= LongByteObject default null;
enddeclare
{
	store# = list.ClientData;
	longimage = store#.Find(key=name);

	if longimage is null then
		bitmap.ErrorStatus = ER_ROWNOTFOUND;
		return bitmap.ErrorStatus;
	endif;
	bitmap.LoadLong(object=longimage);

	return bitmap.ErrorStatus;
}


/*
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
**	Derive a simple item-name from a filehandle
**~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
*/
method NameFromFileHandle(

	filehandle		= varchar(256) not null;
	filestring		= StringObject default null;

)=
declare
	string			= StringObject;
enddeclare
{
	if filestring is not null then
		string = filestring;
	elseif filehandle != '' then
		string.Value = filehandle;
	endif;

	string = string.Split(delimiter=fd$, exactrows=2, backwards=TRUE)[1];
	if string.Value like '%.%' then
		return string.Split(delimiter='.', exactrows=2, backwards=TRUE)[2].Value;
	else
		return string.Value;
	endif;
}


//@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@
//							PROCEDURES
//@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@


procedure CallingFrame(

)=
declare
	parent		= ProcExec default null;
enddeclare
{
	parent = curprocedure.Parent;
	while parent.IsA(class=FrameExec) = FALSE do
		parent = parent.Parent;
	endwhile;
	return parent;
}
