COMPONENT.script:
/*
** Name: customer_browse - displays customer info.
**
** Description:
**	This frame displays customer data and allows user to step backwards
**	as well as forwards through the selected data.  There are menu items
**	to retrieve an individual customer by account number, all customers,
**	or customers whose accounts are closed.  There are also menu items
**	for Next Account and Previous Account.
**
** Called By:
**	main_control, via callframe.
**
** Parameters:
**	(None)
**
** Returns:
**	(None)
*/

initialize () =
declare
  status = integer not null;		/* Returned status from methods */
  reply = StringObject;			/* Reply to prompt for account # */
  ss = SQLSelect;
  selectstring = varchar(200) not null;
  wclause = varchar(60) not null;
  i = smallint not null;
enddeclare

begin

  /*  This part of the query is the same for all of the selects. */

  selectstring =  'select acctno, cphone, cname, caddr, ccity, cstate,'
    + ' czip, cdistrict, cstatus, cacctbal from v_customer'; 

  /*  Must fill in the Expression and IsSelectTarget for every column to be
      retrieved.  */

  ss.Columns[1].Targets[1].Expression = 'customer.acctno';
  ss.Columns[2].Targets[1].Expression = 'customer.cphone';
  ss.Columns[3].Targets[1].Expression = 'customer.cname';
  ss.Columns[4].Targets[1].Expression = 'customer.caddr';
  ss.Columns[5].Targets[1].Expression = 'customer.ccity';
  ss.Columns[6].Targets[1].Expression = 'customer.cstate';
  ss.Columns[7].Targets[1].Expression = 'customer.czip';
  ss.Columns[8].Targets[1].Expression = 'customer.cdistrict';
  ss.Columns[9].Targets[1].Expression = 'customer.cstatus';
  ss.Columns[10].Targets[1].Expression = 'customer.cacctbal';
  for i = 1 to ss.Columns.LastRow do
    ss.Columns[i].Targets[1].IsSelectTarget = TRUE;
  endfor;
end

on click menu.get_menu.initial_account =
begin

  /* Clear all fields on the form & disable some menu items*/

  CurFrame.TopForm.SetToDefault();
  field(menu.get_menu.next_account).CurBias = MB_DISABLED;
  field(menu.get_menu.prev_account).CurBias = MB_DISABLED;

  /* If SQLSelect is active, close it */

  if ss.State  = QS_ACTIVE then
    ss.Close();
  endif;

  /*  Create appropriate where-clause for the SQLSelect */

  if menu.get_menu.initial_account = 2 then
    /* Get single account; first prompt for the customer's account number */
    reply.Value = '';
    status = CurFrame.ReplyPopup(messagetext =
      'Enter the customer''s account number:', reply = reply);
    if status != PU_OK then
      resume;
    endif;
    wclause = ' where acctno = ' + reply.Value;
  elseif menu.get_menu.initial_account = 3 then
    /* Fetch all customers */
    wclause = ' order by cname';
  elseif menu.get_menu.initial_account = 4 then
    /* Fetch those customers whose accounts are closed */
    wclause = ' where cstatus = 0 order by cname';
  endif;

  /*  Put appropriate where-clause on end of select string and open the
      SQLSelect to retrieve the data.  After open, all the data is in the
      cache. */

  ss.Query.Value = selectstring + wclause;
  status = ss.Open(querymode = QY_CACHE, checkcols = TRUE, 
    Scope = CurFrame.Scope);
  if status != ER_OK then
    rollback;
    message 'SQLSelect error, cannot retrieve data.';
    resume;
  endif;
  commit;
  if ss.ErrorNo != 0 and ss.IsDBError = TRUE then /* there was a DB error */
    rollback;
    message 'DBMS error encountered fetching the customer information.';
    resume;
  elseif ss.MaxRow = 0 then		/* no rows found */
    if menu.get_menu.initial_account = 2 then
      message 'Cannot not find customer who has this account number: ' +
	reply.Value + '.';
    else
      message 'No customers selected.';
    endif;
    resume;
  elseif ss.MaxRow > 1 then /* more than 1 row found */
    field(menu.get_menu.next_account).CurBias = MB_ENABLED;
  endif;

  /*  Get the first row from cache to the internal buffers. */

  status = ss.NextRow();

  /*  Move data from internal buffers into fields on the form. */

  status = ss.Load();
end

on click menu.get_menu.next_account =
begin
  /*  Get next row from cache the internal buffers, then move to fields
      on the form. */
  ss.NextRow();
  ss.Load();
  if ss.CurRow = ss.MaxRow then
    field(menu.get_menu.next_account).CurBias = MB_DISABLED;
  endif;
  field(menu.get_menu.prev_account).CurBias = MB_ENABLED;
end

on click menu.get_menu.prev_account =
begin
  /*  Get previous row from cache the internal buffers, then move to fields
      on the form. */
  ss.PrevRow();
  ss.Load();
  if ss.CurRow = 1 then
    field(menu.get_menu.prev_account).CurBias = MB_DISABLED;
  endif;
  field(menu.get_menu.next_account).CurBias = MB_ENABLED;
end

on click close_button,
on windowclose =
begin
  ss.close();
  return;
end

row.script:
ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
