			'to insert a row before it.';
		RESUME;
	ENDIF;
	tf_array.InsertRow(rownumber = table_field.ActiveRow);
	table_field.HasDataChanged = TRUE;
	table_field.ActiveRow = table_field.CurRow - 1;
	tf_array[table_field.ActiveRow]._Rowstate = RS_UNDEFINED;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	IF table_field.ActiveRow = 0 THEN
		MESSAGE 'You must select a row of the table field ' +
			'to delete it.';
		RESUME;
	ENDIF;
	tf_array.SetRowDeleted(rownumber = table_field.ActiveRow);
	table_field.HasDataChanged = TRUE;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	tf_array.Clear();
	table_field.HasDataChanged = TRUE;
END

COMPONENT.script:
/*
** Name: customer_browse - displays customer info.
**
** Description:
**	This frame displays customer data and allows user to step backwards
**	as well as forwards through the selected data.  There are menu items
**	to retrieve an individual customer by account number, all customers,
**	or customers whose accounts are closed.  There are also menu items
**	for Next Account and Previous Account.
**
** Called By:
**	main_control, via callframe.
**
** Parameters:
**	(None)
**
** Returns:
**	(None)
*/

initialize () =
declare
  status = integer not null;		/* Returned status from methods */
  reply = StringObject;			/* Reply to prompt for account # */
  ss = SQLSelect;
  wclause = varchar(60) not null;
  i = smallint not null;
enddeclare

begin

  wclause = '';
  ss.Query.Value =  'select * from v_customer &(wclause) order by cname'; 

  /*  Use SetCols method to initialize the Columns array (column names go into 
      ss.Columns.AsName). Scope is required because we have a parameterized
      where-clause.  Note that you still have to provide the required
      data for at least one target for every column to be retrieved. */

  ss.SetCols(scope = CurFrame.Scope);
  for i = 1 to ss.Columns.LastRow do
    ss.Columns[i].Targets[1].Expression = 'customer.' + ss.Columns[i].AsName;
    ss.Columns[i].Targets[1].IsSelectTarget = TRUE;
  endfor;
end

on click menu.get_menu.initial_account =
begin

  /* Clear all fields on the form & disable some menu items*/

  CurFrame.TopForm.SetToDefault();
  field(menu.get_menu.next_account).CurBias = MB_DISABLED;
  field(menu.get_menu.prev_account).CurBias = MB_DISABLED;

  /* If SQLSelect is active, close it */

  if ss.State  = QS_ACTIVE then
    ss.Close();
  endif;

  /*  Create appropriate where-clause for the SQLSelect */

  if menu.get_menu.initial_account = 2 then
    /* Get single account; first prompt for the customer's account number */
    reply.Value = '';
    status = CurFrame.ReplyPopup(messagetext =
      'Enter the customer''s account number:', reply = reply);
    if status != PU_OK then
      resume;
    endif;
    wclause = ' where acctno = ' + reply.Value;
  elseif menu.get_menu.initial_account = 3 then
    /* Fetch all customers */
    wclause = '';
  elseif menu.get_menu.initial_account = 4 then
    /* Fetch those customers whose accounts are closed */
    wclause = 'where cstatus = 0';
  endif;

  /*  Open the SQLSelect to retrieve the data.  After open, all the data is in 
      cache. */

  status = ss.Open(querymode = QY_CACHE, checkcols = TRUE, 
    scope = CurFrame.Scope);
  if status != ER_OK then
    message 'SQLSelect error, cannot retrieve data';
    resume;
  endif;
  commit;
  if ss.ErrorNo != 0 and ss.IsDBError = TRUE then /* there was a DB error */
    message 'DBMS error encountered fetching the customer information.';
    resume;
  elseif ss.MaxRow = 0 then		/* no rows found */
    if menu.get_menu.initial_account = 2 then
      message 'Cannot not find customer who has this account number: ' +
	reply.Value + '.';
    else
      message 'No customers selected.';
    endif;
    resume;
  elseif ss.MaxRow > 1 then /* more than 1 row found */
    field(menu.get_menu.next_account).CurBias = MB_ENABLED;
  endif;

  /*  Get the first row from cache to the internal buffers. */

  status = ss.NextRow();

  /*  Move data from internal buffers into fields on the form. */

  status = ss.Load();
end

on click menu.get_menu.next_account =
begin
  /*  Get next row from cache the internal buffers, then move to fields
      on the form. */
  ss.NextRow();
  ss.Load();
  if ss.CurRow = ss.MaxRow then
    field(menu.get_menu.next_account).CurBias = MB_DISABLED;
  endif;
  field(menu.get_menu.prev_account).CurBias = MB_ENABLED;
end

on click menu.get_menu.prev_account =
begin
  /*  Get previous row from cache the internal buffers, then move to fields
      on the form. */
  ss.PrevRow();
  ss.Load();
  if ss.CurRow = 1 then
    field(menu.get_menu.prev_account).CurBias = MB_DISABLED;
  endif;
  field(menu.get_menu.next_account).CurBias = MB_ENABLED;
end

on click close_button,
on windowclose =
begin
  ss.close();
  return;
end

row.script:
ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	IF table_field.ActiveRow = 0 THEN
		MESSAGE 'You must select a row of the table field ' +
			'to insert a row before it.';
		RESUME;
	ENDIF;
	tf_array.InsertRow(rownumber = table_field.ActiveRow);
	table_field.HasDataChanged = TRUE;
	table_field.ActiveRow = table_field.CurRow - 1;
	tf_array[table_field.ActiveRow]._Rowstate = RS_UNDEFINED;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	IF table_field.ActiveRow = 0 THEN
		MESSAGE 'You must select a row of the table field ' +
			'to delete it.';
		RESUME;
	ENDIF;
	tf_array.SetRowDeleted(rownumber = table_field.ActiveRow);
	table_field.HasDataChanged = TRUE;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	tf_array.Clear();
	table_field.HasDataChanged = TRUE;
END

COMPONENT.script:
/*
** Name: customer_maintenance	
**
** Description:
**	This frame allows one to insert, update and delete customer data
**	and demonstrates the use of a cursor.  There are menu items to 
**	retrieve an individual customer by account number, all customers, 
**	or customers whose accounts are closed.  
**
** Called By:
**	main_control, via callframe.
**
** Parameters:
**	(None)
**
** Returns:
**	(None)
*/

initialize () =
declare
  status = integer not null;		/* Returned status from methods */
  reply = StringObject;			/* Reply to prompt for account # */
  account_no = integer not null;	/* Actual account number */
  cust_cursor = CursorObject;		/* Cursor for fetching customers */
  error_msg = varchar(80) not null;
enddeclare

begin
  field(update_button).UpdateBias = FB_DIMMED;
  field(delete_button).UpdateBias = FB_DIMMED;
end

on childentry =
begin
  EntryField(CurFrame.TriggerField).BgColor = CC_WHITE;
end

on childexit =
begin
  EntryField(CurFrame.TriggerField).BgColor = CC_PALE_GRAY;
end

on click menu.get_menu.initial_account =
begin
  if CurFrame.TopForm.HasDataChanged = TRUE then
    status = CurFrame.ConfirmPopup(messagetext =
      'Some of the fields on the form have been changed. ' +
      'Doing this operation will lose these changes.');
    if status != PU_OK then
      resume;
    endif;
  endif;

  if cust_cursor.State != CS_CLOSED then
    field(menu.get_menu.next_account).UpdateBias = MB_DISABLED;
    close cust_cursor;
  endif;

  if field(customer).UpdateBias = FB_VISIBLE then
    field(customer).UpdateBias = FB_CHANGEABLE;
  endif;

  field(insert_button).UpdateBias = FB_CHANGEABLE;
  field(update_button).UpdateBias = FB_DIMMED;
  field(delete_button).UpdateBias = FB_DIMMED;

  /* Clear all fields on the form */
  CurFrame.TopForm.SetToDefault();
  CurFrame.InputFocusField = field(customer.cname);

  if menu.get_menu.initial_account = 1 then
    /* Already done in previous statements */
  elseif menu.get_menu.initial_account = 2 then
    /* Get existing account;
    ** First prompt for the customer's account number */
    reply.Value = '';
    status = CurFrame.ReplyPopup(messagetext =
      'Enter the customer''s account number:', reply = reply);
    if status != PU_OK then
      resume;
    endif;

    account_no = int4(reply.Value);

    select acctno as :customer.acctno,
      	   cphone as :customer.cphone,
	   cname as :customer.cname,
	   caddr as :customer.caddr,
	   ccity as :customer.ccity,
	   cstate as :customer.cstate,
	   czip as :customer.czip,
	   cdistrict as :customer.cdistrict,
	   cstatus as :customer.cstatus,
	   cacctbal as :customer.cacctbal
    from v_customer
    where acctno = :account_no;

    if CurFrame.DbSession.ErrorNumber != 0 then
      message 'DBMS error encountered fetching the customer information.';
      menu.get_menu.initial_account = 1;
      resume;
    elseif CurFrame.DBSession.RowCount = 0 then
      message 'Cannot not find customer who has this account number: ' +
	reply.Value + '.';
      menu.get_menu.initial_account = 1;
        resume;
    endif;

    field(insert_button).UpdateBias = FB_DIMMED;
    field(update_button).UpdateBias = FB_CHANGEABLE;
    field(delete_button).UpdateBias = FB_CHANGEABLE;
  else
    if menu.get_menu.initial_account = 3 then
      /* Fetch all customers */
      open cust_cursor
      for select acctno,
                 cphone,
	         cname,
	         caddr,
	         ccity,
	         cstate,
	         czip,
	         cdistrict,
	         cstatus,
	         cacctbal
      from v_customer
      for update of acctno,
		    cphone,
		    cname,
		    caddr,
		    ccity,
		    cstate,
		    czip,
		    cdistrict,
		    cstatus,
		    cacctbal;

      error_msg = 'There are no customers.';

    elseif menu.get_menu.initial_account = 4 then
      /* Fetch those customers whose accounts are closed */
      open cust_cursor
      for select acctno,
	         cphone,
	         cname,
	         caddr,
	         ccity,
	         cstate,
	         czip,
	         cdistrict,
	         cstatus,
	         cacctbal
      from v_customer
      where cstatus = 0
      for update of acctno,
		    cphone,
		    cname,
		    caddr,
		    ccity,
		    cstate,
		    czip,
		    cdistrict,
		    cstatus,
		    cacctbal;

      error_msg = 'There are no customers whose accounts are closed.';
    endif;

    fetch cust_cursor into :customer.acctno = acctno,
			   :customer.cphone = cphone,
			   :customer.cname = cname,
			   :customer.caddr = caddr,
			   :customer.ccity = ccity,
			   :customer.cstate = cstate,
			   :customer.czip = czip,
			   :customer.cdistrict = cdistrict,
			   :customer.cstatus = cstatus,
			   :customer.cacctbal = cacctbal;

    if CurFrame.DBSession.RowCount = 0 then
      message error_msg;
      menu.get_menu.initial_account = 1;
      resume;
    elseif CurFrame.DBSession.ErrorNumber != 0 then
      message 'DBMS error encountered fetching the customer information.';
      menu.get_menu.initial_account = 1;
      resume;
    endif;

    field(insert_button).UpdateBias = FB_DIMMED;
    field(update_button).UpdateBias = FB_CHANGEABLE;
    field(delete_button).UpdateBias = FB_CHANGEABLE;
    field(menu.get_menu.next_account).UpdateBias = MB_ENABLED;
  endif;
end

on click menu.get_menu.next_account =
begin
  if CurFrame.TopForm.HasDataChanged = TRUE then
    status = CurFrame.ConfirmPopup(messagetext =
      'Some of the fields on the form have been changed. ' +
      'Doing this operation will lose these changes.');
    if status != PU_OK then
      resume;
    endif;
  endif;

  fetch cust_cursor into :customer.acctno = acctno,
			 :customer.cphone = cphone,
			 :customer.cname = cname,
			 :customer.caddr = caddr,
			 :customer.ccity = ccity,
			 :customer.cstate = cstate,
			 :customer.czip = czip,
			 :customer.cdistrict = cdistrict,
			 :customer.cstatus = cstatus,
			 :customer.cacctbal = cacctbal;

  if cust_cursor.State = CS_CURRENT then
    field(insert_button).UpdateBias = FB_DIMMED;
    field(update_button).UpdateBias = FB_CHANGEABLE;
    field(delete_button).UpdateBias = FB_CHANGEABLE;
  elseif cust_cursor.State = CS_NO_MORE_ROWS then
    message 'No more accounts.';
    menu.get_menu.initial_account = 1;
    field(menu.get_menu.next_account).UpdateBias = MB_DISABLED;
    field(insert_button).UpdateBias = FB_CHANGEABLE;
    field(update_button).UpdateBias = FB_DIMMED;
    field(delete_button).UpdateBias = FB_DIMMED;
    /* Clear all fields on the form */
    CurFrame.TopForm.SetToDefault();
    CurFrame.InputFocusField = field(customer.cname);
  elseif CurFrame.DBSession.ErrorNumber != 0 then
    message 'DBMS error encountered fetching the customer information.';
    resume;
  endif;

  if field(customer).UpdateBias = FB_VISIBLE then
    field(customer).UpdateBias = FB_CHANGEABLE;
  endif;
  CurFrame.InputFocusField = field(customer.cname);
end

on click insert_button =
begin
  if CurFrame.TopForm.HasDataChanged = FALSE then
    message
      'First enter information about the customer before doing an Insert.';
    resume;
  endif;

  /* Generate a new customer account number */
  update v_highest_number
  set h_number = h_number + 1
  where tbl_name = 'v_customer';

  select h_number as :customer.acctno
  from v_highest_number
  where tbl_name = 'v_customer';

  /* Actually insert customer into database */
  insert into v_customer (acctno,
			  cphone,
			  cname,
			  caddr,
			  ccity,
			  cstate,
			  czip,
			  cdistrict,
			  cstatus,
			  cacctbal)
  values (:customer.acctno,
	  :customer.cphone,
	  :customer.cname,
	  :customer.caddr,
	  :customer.ccity,
	  :customer.cstate,
	  :customer.czip,
	  :customer.cdistrict,
	  :customer.cstatus,
	  :customer.cacctbal);

  if CurFrame.DBSession.ErrorNumber = 0 then
    menu.get_menu.initial_account = 2;
    field(insert_button).UpdateBias = FB_DIMMED;
    field(update_button).UpdateBias = FB_CHANGEABLE;
    field(delete_button).UpdateBias = FB_CHANGEABLE;
    CurFrame.TopForm.HasDataChanged = FALSE;
  else
    message 'Cannot insert customer into the database. ' + 	       
      dbms_error_message();
  endif;
end

on click update_button =
begin
  /* Actually update the customer in the database */
  if menu.get_menu.initial_account > 2 then
    update v_customer
    set acctno = :customer.acctno,
	cphone = :customer.cphone,
	cname = :customer.cname,
	caddr = :customer.caddr,
	ccity = :customer.ccity,
	cstate = :customer.cstate,
	czip = :customer.czip,
	cdistrict = :customer.cdistrict,
	cstatus = :customer.cstatus,
	cacctbal = :customer.cacctbal
    where current of cust_cursor;
  else
    update v_customer
    set acctno = :customer.acctno,
	cphone = :customer.cphone,
	cname = :customer.cname,
	caddr = :customer.caddr,
	ccity = :customer.ccity,
	cstate = :customer.cstate,
	czip = :customer.czip,
	cdistrict = :customer.cdistrict,
	cstatus = :customer.cstatus,
	cacctbal = :customer.cacctbal
    where acctno = :customer.acctno;
  endif;

  if CurFrame.DBSession.ErrorNumber = 0 then
    if menu.get_menu.initial_account > 2 then
      /* Since we have a deferred cursor (by default), we do not allow
      ** additional updates or delete to the current cursor row. */
      CurFrame.InputFocusField.BgColor = CC_PALE_BLUE;
      field(customer).UpdateBias = FB_VISIBLE;
      field(update_button).UpdateBias = FB_DIMMED;
      field(delete_button).UpdateBias = FB_DIMMED;
    else
      commit;
    endif;
    CurFrame.TopForm.HasDataChanged = FALSE;
  else
    message 'Cannot update customer in the database. ' + dbms_error_message();
  endif;
end

on click delete_button =
begin
  if menu.get_menu.initial_account > 2 then
    delete from v_customer
    where current of cust_cursor;
  else
    delete from v_customer
    where acctno = :customer.acctno;
  endif;

  if CurFrame.DBSession.ErrorNumber = 0 then
    if menu.get_menu.initial_account > 2 then
       /* Still in get next mode */
      CurFrame.InputFocusField.BgColor = CC_PALE_BLUE;
      field(customer).UpdateBias = FB_VISIBLE;
    else
      commit;
      menu.get_menu.initial_account = 1;
      field(insert_button).UpdateBias = FB_CHANGEABLE;
      CurFrame.InputFocusField = field(customer.cname);
    endif;
    field(update_button).UpdateBias = FB_DIMMED;
    field(delete_button).UpdateBias = FB_DIMMED;
    /* Clear all fields on the form */
    CurFrame.TopForm.SetToDefault();
  else
    message 'Cannot update customer in the database. ' + dbms_error_message();
  endif;
end

on click close_button,
on windowclose =
begin
  if CurFrame.TopForm.HasDataChanged = TRUE then
    status = CurFrame.ConfirmPopup(messagetext =
      'Some of the fields on the form have been changed. ' +
      'Closing the frame will lose these changes.');
  else
    status = PU_OK;
  endif;

  if status = PU_OK then
    if cust_cursor.State != CS_CLOSED then
      close cust_cursor;
    endif;
    commit;

    return;
  endif;
end

row.script:
ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	IF table_field.ActiveRow = 0 THEN
		MESSAGE 'You must select a row of the table field ' +
			'to insert a row before it.';
		RESUME;
	ENDIF;
	tf_array.InsertRow(rownumber = table_field.ActiveRow);
	table_field.HasDataChanged = TRUE;
	table_field.ActiveRow = table_field.CurRow - 1;
	tf_array[table_field.ActiveRow]._Rowstate = RS_UNDEFINED;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	IF table_field.ActiveRow = 0 THEN
		MESSAGE 'You must select a row of the table field ' +
			'to delete it.';
		RESUME;
	ENDIF;
	tf_array.SetRowDeleted(rownumber = table_field.ActiveRow);
	table_field.HasDataChanged = TRUE;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	tf_array.Clear();
	table_field.HasDataChanged = TRUE;
END

COMPONENT.script:
/*
** Name: customer_maintenance2	- Allows entering and updating customer info.
**
** Description:
**	This frame allows one to insert, update and delete customer data.
**	There are menu items to retrieve an individual customer by account
**	number, all customers, or customers whose accounts are closed.  There
**	are also menu items for Next Account and Previous Account.
**
**	Frame uses QueryObject to keep cache in sync after 
**	insert/update/delete.
**
**
** Called By:
**	main_control, via callframe.
**
** Parameters:
**	(None)
**
** Returns:
**	(None)
** History:
**	12-oct-95(timt) - remove AsName specification since CA-INGRES
**			  DESKTOP does not support as name in order by clause.
*/

initialize () =
declare
  status = integer not null;		/* Returned status from methods */
  reply = StringObject;			/* Reply to prompt for account # */
  account_no = integer not null;	/* Actual account number */
  error_msg = varchar(80) not null;
  qo = QueryObject;
  i = smallint not null;
enddeclare

begin
  CurFrame.DataEntryErrorHandler = CurFrame.Scope.GetProcHandle
    (Name = 'another_handlerproc');

  field(update_button).CurBias = FB_DIMMED;
  field(delete_button).CurBias = FB_DIMMED;

  /*  Populate QueryObject attributes that are the same for all selects. 
      Must fill in ColumnName and Targets[1] attributes for each column to 
      be retrieved or updated.  Must supply TableName and set IsSelectTarget
      and IsUpdateTarget appropriately. Determine each column's operation
      during update and delete by setting IsUpdateTarget, IsUpdateWhere,
      and IsDeleteWhere.  If grouping or ordering, set the GroupBy or 
      OrderBy attributes and provide AsName for every column to be used in
      the ordering or grouping.*/

  qo.Tables[1].TableName = 'v_customer';

  qo.Columns[1].ColumnName = 'acctno';
  qo.Columns[2].ColumnName = 'cphone';
  qo.Columns[3].ColumnName = 'cname';
  qo.Columns[4].ColumnName = 'caddr';
  qo.Columns[5].ColumnName = 'ccity';
  qo.Columns[6].ColumnName = 'cstate';
  qo.Columns[7].ColumnName = 'czip';
  qo.Columns[8].ColumnName = 'cdistrict';
  qo.Columns[9].ColumnName = 'cstatus';
  qo.Columns[10].ColumnName = 'cacctbal';
  for i = 1 to qo.Columns.LastRow do
    qo.Columns[i].Targets[1].Expression = 'customer.' +
      qo.Columns[i].ColumnName;
    qo.Columns[i].Targets[1].IsSelectTarget = TRUE;
    qo.Columns[i].Targets[1].IsUpdateTarget = TRUE;
    qo.Columns[i].FromTable = qo.Tables[1];                
  endfor;

  /*  acctno is different; it cannot be updated, but will be part of
      the where-clause for a delete or update. */

  qo.Columns[1].Targets[1].IsUpdateTarget = FALSE;
  qo.Columns[1].Targets[1].IsUpdateWhere = TRUE;
  qo.Columns[1].Targets[1].IsDeleteWhere = TRUE;
  qo.Scope = CurFrame.Scope; 

  /*  When more than one account retrieved, we will order them by cname,
      the third column. */

  qo.Columns[3].OrderBy = 1;

end;

on click menu.get_menu.initial_account =
begin
  if CurFrame.TopForm.HasDataChanged = TRUE then
    status = CurFrame.ConfirmPopup(messagetext =
      'Some of the fields on the form have been changed. ' +
      'Doing this operation will lose these changes.');
    if status != PU_OK then
      resume;
    endif;
  endif;

  /* If Querybject is active, close it */

  if qo.State  = QS_ACTIVE then
    qo.Close();
    field(menu.get_menu.next_account).CurBias = MB_DISABLED;
    field(menu.get_menu.prev_account).CurBias = MB_DISABLED;
  endif;

  field(insert_button).CurBias = FB_CHANGEABLE;
  field(update_button).CurBias = FB_DIMMED;
  field(delete_button).CurBias = FB_DIMMED;

  /* Clear all fields on the form */
  CurFrame.TopForm.SetToDefault();
  CurFrame.InputFocusField = field(customer.cname);

  if menu.get_menu.initial_account = 1 then 
    /*  New account; insert is only possible operation */
    resume;
  elseif menu.get_menu.initial_account = 2 then
    /*  Will be getting a single existing account; prompt for the customer's
	account number. */
    reply.Value = '';
    status = CurFrame.ReplyPopup(messagetext =
      'Enter the customer''s account number:', reply = reply);
    if status != PU_OK then
      resume;
    else  
      qo.RunTimeWhere.Value = 'acctno = ' + reply.Value;
      error_msg = 'No customer with account No. ' + reply.Value;
    endif;
  elseif menu.get_menu.initial_account = 3 then
    qo.RunTimeWhere.Value = '';	 /* Fetch all customers */
    error_msg = 'There are no customers.';
  else
    qo.RunTimeWhere.Value = 'cstatus = $CLOSED'; /* Fetch closed customers */
    error_msg = 'There are no customers whose accounts are closed.';
  endif;
  status = qo.Open(querymode = QY_CACHE);
  if status != ER_OK then
    message 'Error opening QueryObject.';
    resume;
  endif;

  /*  Since it is unlikely that another user will also be updating customer
      info, we will not hold locks on the selected data. */
  commit;
  if qo.ErrorNo != 0 and qo.IsDBError = TRUE then /* there was a DB error */
    message 'DBMS error encountered fetching the customer information.';
  elseif qo.MaxRow = 0 then		/* no rows found */
    message error_msg;
  else 	
    /*  At least one row found.  Get the first row from cache to the 
        internal buffers and then move data into fields on the form. */
    status = qo.NextRow();
    status = qo.Load();

    field(insert_button).CurBias = FB_DIMMED;
    field(update_button).CurBias = FB_CHANGEABLE;
    field(delete_button).CurBias = FB_CHANGEABLE;
    if qo.MaxRow > 1 then /* more than 1 row found */
      field(menu.get_menu.next_account).CurBias = MB_ENABLED;
    endif;
  endif;
end

on click menu.get_menu.next_account =
begin
  if CurFrame.TopForm.HasDataChanged = TRUE then
    status = CurFrame.ConfirmPopup(messagetext =
      'Some of the fields on the form have been changed. ' +
      'Doing this operation will lose these changes.');
    if status != PU_OK then
      resume;
    else
      CurFrame.TopForm.HasDataChanged = FALSE;
    endif;
  endif;
  /*  Get and load the next row */
  qo.NextRow();
  qo.Load();
  if qo.CurRow = qo.MaxRow then
    field(menu.get_menu.next_account).CurBias = MB_DISABLED;
  endif;
  field(menu.get_menu.prev_account).CurBias = MB_ENABLED;
end

on click menu.get_menu.prev_account =
begin
  if CurFrame.TopForm.HasDataChanged = TRUE then
    status = CurFrame.ConfirmPopup(messagetext =
      'Some of the fields on the form have been changed. ' +
      'Doing this operation will lose these changes.');
    if status != PU_OK then
      resume;
    else
      CurFrame.TopForm.HasDataChanged = FALSE;
    endif;
  endif;
  /*  Get and load the previous row */
  qo.PrevRow();
  qo.Load();
  if qo.CurRow = 1 then
    field(menu.get_menu.prev_account).CurBias = MB_DISABLED;
  endif;
  field(menu.get_menu.next_account).CurBias = MB_ENABLED;
end

on click insert_button =
begin
  if CurFrame.TopForm.HasDataChanged = FALSE then
    message 'Enter information about the customer before doing an Insert.';
  else

    /* Generate a new customer account number */
    :customer.acctno = CurFrame.DBSession.SequenceValue(
       table_name = 'v_customer', column_name = 'acctno');

    /* Actually insert customer into database */
    insert into v_customer (acctno, cphone, cname, caddr, ccity, cstate,
      czip, cdistrict, cstatus, cacctbal)
    values (:customer.acctno, :customer.cphone, :customer.cname, 
      :customer.caddr, :customer.ccity, :customer.cstate, :customer.czip,
      :customer.cdistrict, :customer.cstatus, :customer.cacctbal);
    if CurFrame.DBSession.ErrorNumber != 0 then
      error_msg = 'Cannot insert customer into the database. ' +
	dbms_error_message();
      rollback;
      message error_msg;  
    else
      commit;
      CurFrame.TopForm.SetToDefault();
    endif;
  endif;
end

on click update_button =
begin
  /* Actually update the customer in the database */
  status = qo.DBUpdate(ZeroRowsIsError = TRUE, MaxRows = 1);
  if status != ER_OK or qo.ErrorNo != 0 then 	/* no row updated */
    rollback;
    message 'DBMS error ' + varchar(qo.ErrorNo) 
	+ ' encountered updating customer information.';
  else
    qo.CommitToCache();
    commit;
    CurFrame.TopForm.HasDataChanged = FALSE;
  endif;
end

on click delete_button =
begin
  /*  Delete the customer from the database */
  status = qo.DBDelete(ZeroRowsIsError = TRUE, MaxRows = 1);
  if status != ER_OK or qo.ErrorNo != 0 then 	/* no row deleted */
    rollback;
    message 'DBMS error ' + varchar(qo.ErrorNo) 
	+ ' encountered deleting customer information.';
  else
    qo.CommitToCache();
    commit;
    CurFrame.TopForm.HasDataChanged = FALSE;
    if qo.MaxRow = 0 then
      message 'No more data to display.';
      CurFrame.TopForm.SetToDefault();
      field(update_button).CurBias = FB_DIMMED;
      field(delete_button).CurBias = FB_DIMMED;
    else

      /*  When row is deleted from an array, the rows after it is moved
          up to its place, and so on.  So if a Load is done without a
	  fetch, the row loaded will be the one following the one deleted.
	  If there are no more rows, it will load the previous row. */

      qo.Load();
      if qo.CurRow = qo.MaxRow then
    	field(menu.get_menu.next_account).CurBias = MB_DISABLED;
      endif;
      if qo.CurRow = 1 then
    	field(menu.get_menu.prev_account).CurBias = MB_DISABLED;
      endif;
    endif;
  endif;
end

on click close_button,
on windowclose =
begin
  if CurFrame.TopForm.HasDataChanged = TRUE then
    status = CurFrame.ConfirmPopup(messagetext =
      'Some of the fields on the form have been changed. ' +
      'Closing the frame will lose these changes.');
  else
    status = PU_OK;
  endif;

  if status = PU_OK then
    qo.Close();
    return;
  endif;
end

row.script:
ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	IF table_field.ActiveRow = 0 THEN
		MESSAGE 'You must select a row of the table field ' +
			'to insert a row before it.';
		RESUME;
	ENDIF;
	tf_array.InsertRow(rownumber = table_field.ActiveRow);
	table_field.HasDataChanged = TRUE;
	table_field.ActiveRow = table_field.CurRow - 1;
	tf_array[table_field.ActiveRow]._Rowstate = RS_UNDEFINED;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	IF table_field.ActiveRow = 0 THEN
		MESSAGE 'You must select a row of the table field ' +
			'to delete it.';
		RESUME;
	ENDIF;
	tf_array.SetRowDeleted(rownumber = table_field.ActiveRow);
	table_field.HasDataChanged = TRUE;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	tf_array.Clear();
	table_field.HasDataChanged = TRUE;
END

COMPONENT.script:
/*
**  Frame:          dynamicframe
**  DynamicFrame has a form with an option field that provides the user 
**  a listing of table names owned by the user or the dba that do not
**  begin with II.  The option field  is called table_choices.
**
**  The menu-item Go generates a frame to execute a query on the 
**  selected table and lists rows in simple fields.  The generated frame 
**  has menu items for updating and browsing the data and fetching the
**  next row for display.  The frame is automatically compiled and run
**  after generation.
**
**  The menu items are:
**     File.Go - generate a frame that allows query/update of the data 
**      in the table in table_choices and execute the frame.  If the frame has 
**      already been generated, simply execute it.
**     File.Close - return from this frame.
**
**  The generated frame is called '<tablename>_frame', and contains
**  the following source code:
**
**      INITIALIZE (table_cursor = CursorObject) =
**          BEGIN
**              OPEN table_cursor FOR     
**                  column1,
**                  column2,
**                      . . .
**              FROM table_name
**              FOR DIRECT UPDATE OF 
**                  column1,
**                  column2,
**                      . . .
**              CurFrame.SendUserEvent(eventname = 'Next');
**          END;
**      ON CLICK file_menu.commit_menu = 
**          BEGIN
**              CLOSE table_cursor;
**              COMMIT WORK;
**              RETURN;
**          END;
**      ON CLICK file_menu.rollback_menu = 
**          BEGIN
**              CLOSE table_cursor;
**              ROLLBACK WORK;
**              RETURN;
**          END;
**      ON CLICK edit_menu.delete_menu = 
**          BEGIN
**              DELETE FROM table_name WHERE CURRENT OF table_cursor;
**              CurFrame.SendUserEvent(eventname = 'Next');
**          END;
**      ON CLICK edit_menu.update_menu = 
**          BEGIN
**              UPDATE table_name
**              SET     column1 = :column1,
**                      column2 = :column2,
**                          . . .
**              WHERE CURRENT OF table_cursor;
**              CurFrame.SendUserEvent(eventname = 'Next');
**          END;
**      ON CLICK edit_menu.next_menu,
**      ON USEREVENT 'Next' =
**          BEGIN
**              FETCH table_cursor INTO 
**                  :column1 = column1,
**                  :column2 = column2,
**                      . . .
**              IF table_cursor.State = CS_NO_MORE_ROWS THEN
**                  MESSAGE 'No more rows.';
**              ENDIF;
**          END;
**
**  The generated frame's form contains an entry field for each column in 
**  the table. The entry field has the same name as the corresponding table's .
**  column. 
**
**  The Generated_Frame_List global variable keeps track of which 
**  frames have already been generated, so that tables already generated
**  need not be regenerated.  
*/

INITIALIZE () =
declare
    /* Needed for loading the option list */
    i = INTEGER NOT NULL;                   /* temp index */

    /* Needed in generation of frame */
    numcolumns = INTEGER NOT NULL;          /* number of columns in tab */
    widthchar = INTEGER NOT NULL;           /* width of one character */
    heightchar = INTEGER NOT NULL;          /* height of one character */
    fieldleft = INTEGER NOT NULL;           /* left edge of field */
    trimleft = INTEGER NOT NULL;            /* left edge of trim */
    fieldtop = INTEGER NOT NULL;            /* top of next field */
    fieldheight = INTEGER NOT NULL;         /* height of a field */
    maxformheight = INTEGER NOT NULL;       /* maximum height of form */
    maxformwidth = INTEGER NOT NULL;        /* fixed width of form */
    vertspace = INTEGER NOT NULL;           /* space between lines */
    trimspace = INTEGER NOT NULL;           /* space between trim/field */
    dbaname = VARCHAR(32) NOT NULL;         /* name of dba */
    username = VARCHAR(32) NOT NULL;        /* name of user */

    /* This set of variables is used for generating the source code */
    selectstring = VARCHAR(2000) NOT NULL;  /* SELECT statement string */
    fetchstring = VARCHAR(2000) NOT NULL;   /* FETCH statement string */
    updatestring = VARCHAR(2000) NOT NULL;  /* UPDATE statement string */
    selectupdatestring = VARCHAR(2000) NOT NULL;    /* update clause string */

    /* These hold the dictionary information of the table columns */
    tmp_table_name = VARCHAR(32) NOT NULL;  /* used in select choices */
    columnname = VARCHAR(32) NOT NULL;      /* used in getting column names */
    columndatatype = VARCHAR(32) NOT NULL;  /* used in getting columns */
    columnlength = INTEGER NOT NULL;        /* used in getting columns */

    /* These are used as holders of form objects, etc. */
    test_frame = FrameSource default null;   /* The frame being generated */
    top_menu = MenuGroup default null;       /* The menu being generated */
    test_menu = MenuButton default null;     /* The menu item */
    field_ptr = EntryField default null;     /* Ptr to entry field */
    trim_ptr = FreeTrim default null;        /* The trim ptr */

    test_frame_name = varchar(32) not null  /* Name of generated frame */
enddeclare 

  begin
    /* Get the user and dba name */
    select dba_name as :dbaname 
    from   iidbconstants;
    /* Release locks on the iidbconstants table. */
    commit;

    /* Load up the list of non II table names in option field */
    field(table_choices).ValueList.ChoiceItems.Clear();

    i = 1;
    /* Note:  Cannot use FIELD function in SELECT */
    select distinct table_name as :tmp_table_name
    from        iitables    
    where       table_owner = :dbaname
    begin
        field(table_choices).ValueList.ChoiceItems[i].EnumText=tmp_table_name;
        field(table_choices).ValueList.ChoiceItems[i].EnumValue = i;
        i = i + 1;
    end;
    commit;
    /* ChoiceLists must be explicitly updated in the display */
    field(table_choices).UpdChoiceList();

    /* Setting up initial values */
    widthchar = 100;
    heightchar = 200;
    trimleft = 100;
    trimspace = 100;
    maxformheight = 3000;
    maxformwidth = 9000;
    fieldleft = trimleft + (33*widthchar) + trimspace;
    vertspace = 250;
  end;

on click menu.file_menu.go_menu =
  begin
    /* First check to see if a frame for this table has already
    ** been generated.  If so, simply execute it.  If not,
    ** generate it first and then execute it */
    if Generated_Frame_List.LastRow > 0 then
        /* There is at least one generated frame. */
        for i = 1 to Generated_Frame_List.LastRow do
            if Generated_Frame_List[i].table_name = table_choices then
                /* Frame already there.  Execute it */
                callframe :Generated_Frame_List[i].frame_name;
                resume;
            endif;
            i = i + 1;
        endfor;
    endif;

    /* No frame found.  Generate a new one. */
    fieldtop = 200;                             /* Starting field on frame */
    test_frame = FrameSource.Create();          /* temporary frame source */

    /* Fill in the Frame Information */
    test_frame_name = table_choices + '_frame';
    test_frame.Name = test_frame_name;

    test_frame.DataType = 'integer4';           /* Should be default */
    test_frame.IsNullable = TRUE;               /* Should be default */
    test_frame.WindowTitle = 'Edit Data For Table: ' + table_choices;
    test_frame.CurMode = FM_UPDATE;
    test_frame.WindowPlacement = WP_FLOATING;

    /* Now fill in form information */
    test_frame.TopForm = FrameForm.Create();    /* create the form */

    /* Get the list of columns in the form */
    numcolumns = 0;
    repeated select column_name as :columnname,
                    column_datatype as :columndatatype,
                    column_length as :columnlength 
    from    iicolumns
    where   table_name = :table_choices and table_owner = :dbaname
    begin 
        /* Add column name to SQL statement strings being generated */
        columnname = trim(columnname);
        columndatatype = lowercase(trim(columndatatype));
        if numcolumns <= 0 then
            /* This is the first column found.  Insert statement headers */
            selectstring = 'open table_cursor for select ' + 
                HC_NEWLINE + HC_TAB + HC_TAB + HC_TAB + columnname;
            fetchstring = 'fetch table_cursor into ' +
                HC_NEWLINE + HC_TAB + HC_TAB + HC_TAB +
                ':' + columnname + ' = '  + columnname;
            selectupdatestring = 'for direct update of ' +
                HC_NEWLINE + HC_TAB + HC_TAB + HC_TAB + columnname;
            updatestring = 'UPDATE ' + table_choices +
                HC_NEWLINE + HC_TAB + HC_TAB + 'SET ' + HC_TAB +
                columnname + ' = :' + columnname;
        else
            /* Not the first column.  Concatenated to strings */
            selectstring = selectstring + ',' + HC_NEWLINE + HC_TAB + 
                HC_TAB + HC_TAB + columnname;
            fetchstring = fetchstring + ',' + HC_NEWLINE + HC_TAB + 
                HC_TAB + HC_TAB + ':' + columnname + ' = ' + columnname;
            updatestring = updatestring + ',' + HC_NEWLINE + HC_TAB +
                HC_TAB + HC_TAB + columnname + ' = :' + columnname;
            selectupdatestring = selectupdatestring + ',' + HC_NEWLINE+
                HC_TAB + HC_TAB + HC_TAB + columnname;
        endif;
        numcolumns = numcolumns + 1;

        /* Now generate a title and field for the column.  First, the title */
        trim_ptr = FreeTrim.Create();
        trim_ptr.XLeft = trimleft;
        trim_ptr.Ytop = fieldtop;
        trim_ptr.TextValue = trim(
            trim(uppercase(left(columnname,1))) + 
            trim(lowercase(shift(columnname,-1))) + ':');
        trim_ptr.IsBold = TRUE;
        trim_ptr.TypeSize = 8;
        trim_ptr.Width = 33 * widthchar;
        trim_ptr.Height = heightchar;
        trim_ptr.ParentField = test_frame.TopForm;      /* attach to form */

        /* Construct the field.  EntryFields only. */
        field_ptr = EntryField.Create();
        field_ptr.Name = columnname;
        field_ptr.IsNullable = TRUE;
	field_ptr.IsBold = TRUE;
	field_ptr.TypeSize = 8;
        field_ptr.XLeft = fieldleft;
        field_ptr.YTop = fieldtop; 

        /* Construct the right datatypes */
        fieldheight = 1;
        /* Check first for non-character data types. */
        if columndatatype = 'integer' then
            columnlength = 13;
            field_ptr.DataType = 'integer';
            field_ptr.FormatString = 'f10';
            field_ptr.IsMultiLine = FALSE;
        elseif columndatatype = 'float' then
            columnlength = 15;
            field_ptr.DataType = 'f8';
            field_ptr.FormatString = 'f12.2';
            field_ptr.IsMultiLine = FALSE;
        elseif columndatatype = 'date' then
            columnlength = 25;
            field_ptr.DataType = 'date';
            field_ptr.FormatString = 'd"Feb 3, 1901"';
            field_ptr.IsMultiLine = FALSE;
        elseif columndatatype = 'money' then
            columnlength = 15;
            field_ptr.DataType = 'money';
            field_ptr.FormatString = '"$$$,$$$,$$$.nn"';
            field_ptr.IsMultiLine = FALSE;
        else    /* All character datatypes follow */
            field_ptr.DataType = 'varchar(' + varchar(columnlength) + ')';
            /* See if we need a multiline entry field */
            if columnlength > 50 then
                field_ptr.IsMultiLine = TRUE;
                field_ptr.FocusBehavior = FT_TABTO;
                fieldheight = columnlength / 50 + 1;
                columnlength = 50;
                if fieldheight > 4 then
                  field_ptr.HasScrollBar = TRUE;
                  fieldheight = 4;
                endif;
            else
                field_ptr.IsMultiLine = FALSE;
                field_ptr.FocusBehavior = FT_TABTO;
                if columnlength < 5 then
                    columnlength = 5;
                endif;
            endif; 
        endif;

        /* Now place it on the form. */
        field_ptr.Width = columnlength * widthchar;
        field_ptr.Height = heightchar * fieldheight;
        field_ptr.OutlineWidth = LW_DEFAULT;
        field_ptr.ParentField = test_frame.TopForm;     /* attach to form */
        fieldtop = fieldtop + field_ptr.Height + vertspace;
    end;            /* End of SELECT loop */
    commit;

    /* Now set the form height and width */
    test_frame.WindowHeight = fieldtop + vertspace;
    test_frame.WindowWidth = maxformwidth;
    if test_frame.WindowHeight > maxformheight then
        test_frame.WindowHeight = maxformheight;
        test_frame.HasScrollbars = TRUE;
    else
        test_frame.HasScrollbars = FALSE;
    endif;

    /* Now complete the SELECT and FETCH strings */
    selectstring = selectstring + HC_NEWLINE + HC_TAB + HC_TAB +
        'from ' + table_choices;
    selectupdatestring = selectupdatestring + ';';
    fetchstring = fetchstring + ';';

    /* Create the menu items.  First, create the File menu */
    test_frame.StartMenu = MenuBar.Create();
    top_menu = MenuGroup.Create();                  /* File Menu */
    top_menu.TextLabel = 'File';
    top_menu.name = 'file_menu';

    /* Create the File.Commit menu item */
    test_menu = MenuButton.Create();                /* Close menu item */
    test_menu.name = 'commit_menu';
    test_menu.TextLabel = 'Commit and Close';
    test_menu.SpeedKey = SK_CLOSE;
    test_menu.ParentMenuGroup = top_menu;           /* Attach to file menu */

    /* Create the File.Rollback menu item */
    test_menu = MenuButton.Create();                /* Close menu item */
    test_menu.name = 'rollback_menu';
    test_menu.TextLabel = 'Rollback and Close';
    test_menu.ParentMenuGroup = top_menu;           /* Attach to file menu */

    top_menu.ParentMenuGroup = test_frame.StartMenu;

    /* Now, create the Edit menu */
    top_menu = MenuGroup.Create();                  /* Edit Menu */
    top_menu.TextLabel = 'Edit';
    top_menu.name = 'edit_menu';

    /* Create the Edit.Delete menu item */
    test_menu = MenuButton.Create();                /* Delete menu item */
    test_menu.name = 'delete_menu';
    test_menu.TextLabel = 'Delete and Next';
    test_menu.SpeedKey = SK_DELETE;
    test_menu.ParentMenuGroup = top_menu;           /* Attach to Edit menu */

    /* Create the Edit.Update menu item */
    test_menu = MenuButton.Create();                /* Update menu item */
    test_menu.name = 'update_menu';
    test_menu.TextLabel = 'Update and Next';
    test_menu.ParentMenuGroup = top_menu;           /* Attach to Edit menu */

    /* Create the Edit.Next menu item */
    test_menu = MenuButton.Create();                /* Next menu item */
    test_menu.name = 'next_menu';
    test_menu.SpeedKey = SK_NEXT;
    test_menu.TextLabel = 'Next';
    test_menu.ParentMenuGroup = top_menu;           /* Attach to Edit menu */

    top_menu.ParentMenuGroup = test_frame.StartMenu;    /* Attach to Frame */

    /* Now generate the source code */
    test_frame.Script = StringObject.Create();

    /* Set up the initialize block */
    test_frame.Script.ConcatVarchar(text =
        'initialize (table_cursor = CursorObject) =' + HC_NEWLINE +
        HC_TAB + 'BEGIN' + HC_NEWLINE +
        HC_TAB + HC_TAB + selectstring + HC_NEWLINE);
    test_frame.Script.ConcatVarchar(text = 
        HC_TAB + HC_TAB + selectupdatestring + HC_NEWLINE +
        HC_TAB + HC_TAB +
          'CurFrame.SendUserEvent(eventname = ''Next'');' + HC_NEWLINE +
        HC_TAB + 'END;' + HC_NEWLINE + HC_NEWLINE);

    /* Set up the file.commit script */
    test_frame.Script.ConcatVarchar(text =
        'on click file_menu.commit_menu = ' + HC_NEWLINE +
        HC_TAB + 'BEGIN' + HC_NEWLINE +
        HC_TAB + HC_TAB + 'close table_cursor;' + HC_NEWLINE +
        HC_TAB + HC_TAB + 'commit work;' + HC_NEWLINE +
        HC_TAB + HC_TAB + 'return;' + HC_NEWLINE +
        HC_TAB + 'end;' + HC_NEWLINE + HC_NEWLINE);

    /* Set up the file.rollback script */
    test_frame.Script.ConcatVarchar(text =
        'on click file_menu.rollback_menu = ' + HC_NEWLINE +
        HC_TAB + 'begin' + HC_NEWLINE +
        HC_TAB + HC_TAB + 'close table_cursor;' + HC_NEWLINE +
        HC_TAB + HC_TAB + 'rollback work;' + HC_NEWLINE +
        HC_TAB + HC_TAB + 'return;' + HC_NEWLINE +
        HC_TAB + 'end;' + HC_NEWLINE + HC_NEWLINE);

    /* Set up the edit.delete script */
    test_frame.Script.ConcatVarchar(text =
        'on click edit_menu.delete_menu = ' + HC_NEWLINE +
        HC_TAB + 'begin' + HC_NEWLINE +
        HC_TAB + HC_TAB + 'delete from ' + table_choices + 
          ' where current of table_cursor;' + HC_NEWLINE +
        HC_TAB + HC_TAB +
          'CurFrame.SendUserEvent(eventname = ''Next'');' + HC_NEWLINE+
        HC_TAB + 'end;' + HC_NEWLINE + HC_NEWLINE);

    /* Set up the edit.update script */
    test_frame.Script.ConcatVarchar(text =
        'on click edit_menu.update_menu = ' + HC_NEWLINE +
        HC_TAB + 'begin' + HC_NEWLINE +
        HC_TAB + HC_TAB + updatestring + HC_NEWLINE +
        HC_TAB + HC_TAB + 'where current of table_cursor;' +HC_NEWLINE+
        HC_TAB + HC_TAB +
          'CurFrame.SendUserEvent(eventname = ''Next'');' + HC_NEWLINE+
        HC_TAB + 'end;' + HC_NEWLINE + HC_NEWLINE);

    /* Set up the edit.next script */
    test_frame.Script.ConcatVarchar(text =
        'on click edit_menu.next_menu,' + HC_NEWLINE +
        'on userevent ''Next'' =' + HC_NEWLINE +
        HC_TAB + 'begin' + HC_NEWLINE +
        HC_TAB + HC_TAB + fetchstring + HC_NEWLINE +
        HC_TAB + HC_TAB +
          'if table_cursor.State = CS_NO_MORE_ROWS then' + HC_NEWLINE +
        HC_TAB + HC_TAB + HC_TAB +
         'message ''No more rows.'';' + HC_NEWLINE +
        HC_TAB + HC_TAB + 'endif;' + HC_NEWLINE +
        HC_TAB + 'end;' + HC_NEWLINE + HC_NEWLINE);

    /* Write out the dynamic frame script to a file for debugging. */
    /* test_frame.Script.WriteToFile('test.script'); */

    /* Now add the frame to the application. */
    test_frame.ParentApplication = CurFrame.ObjectSource.ParentApplication;

    /* Now that the frame has been created, add it to the list
    ** of frames that have been generated, stored in the global
    ** variable array Generated_Frame_List. */
    i = Generated_Frame_List.LastRow + 1; 
    Generated_Frame_List[i].table_name = table_choices;
    Generated_Frame_List[i].frame_name = table_choices + '_frame';
    Generated_Frame_List[i].frame_source = test_frame;

    /* Now execute the frame */
    test_frame_name = table_choices + '_frame';
    callframe :test_frame_name;

    /* Write out any compilation errors to a file for debugging */
    /*
    if test_frame.compile_errors is not null then
        test_frame.Compile_Errors.WriteToFile('test.errors');
        Generated_Frame_List.RemoveRow(rownumber = i);
    endif;
    */
  end;

on click menu.file_menu.close_menu =
  begin
    return;
  end;

/*********************************************************************************
**  Frame DynamicQuery illustrates the following dynamic features:
**
**	-- using frame mode to change frame appearance
**
**	-- dynamically creating composite and simple fields in the currently  
**	   running frame; getting values from these fields using the 
**	   GetFieldValue function.
**
**	-- insert/delete of columns at run time in a TableField created in the 
**	   Application Editor.  Note that even though the TableField 
**	   was not created dynamically, its variable is not declared until 
**	   drun time.  One consequence:  You can specify a ControlButton for 
**	   the TableField, but code for its menu items will not be compiled
**	   not be compiled - even if you put it in the frame script.  If you
**	   really want a ControlButton for tbl, create it independently.
**
**	-- implicitly creating a dynamic user class for the array
**	   underlying tbl by declaring and undeclaring field variables at 
**	   run time.
**
**	-- Creating a QueryObject to execute dynamic SQL statements.
**	
**	-- using DynExpr to assign values to a dynamic TableField
**
**  UserClasses required:
**
**	ColumnData - TableField column_choices is mapped to this user class, 
**	which stores additional info about columns in a selected table that 
**	should not be displayed to the user; for example, datatype.
**
**	Qualinfo - the class of EntryField ClientData objects, used to form
**	a qualification string for the QueryObject where-clause.  Has two
**	varchar attributes:  String1 and String2.
**
**  Inputs:	   none
**
**  Return value:  none
**  
**  Description: 
**
**	Frame opens in User1 mode.  User selects a database table (from 
**	OptionField table_choices) to display in TableField tbl.  When user 
**	selects a table, a SQLSelect is created.  Data from the Columns array
**	used to load TableField column_choices and create a user class 
**	(newclass) containing the columns as attributes.  The frame then 
**	switches to Query mode; table_choices becomes invisible and TableField 
**	column_choices appears.  
**
**	In Query mode, user can select columns to be displayed and also columns
**	for search criteria.  When user clicks on Go button, a Matrixfield 
**	(mfield) is created dynamically and fields for the search criteria 
**	are added to it.  Appropriate columns are also added to TableField 
**	(tbl).  The frame then switches to Read mode; column_choices becomes   
**	invisible, while mfield and tbl appear.  If user clicks NewTable 
**	button instead, frame simply returns to User1 mode.
**  
**	In Read mode, if there were no search criteria, tbl is loaded 
**	immediately with all the rows from the specified table.  Otherwise, 
**	after search criteria are entered and user clicks Go button again, 
**	the where clause is created and the QueryObject is opened to load
**	tbl.  User can enter new search criteria and repeat Go operation at  
**	any time; this changes the QueryObject.RunTimeWhere, but other   
**	dynamic objects are unchanged.
**
**	In Read mode user also can click NewColumns button to select different 
**	columns of the same table for display.  Frame switches back to Query 
**	mode; the MatrixField is unchanged, but tbl and query object are 
**	modified when user selects Go.  
**
**	In Read mode user can also click NewTable button to select a new  
**	new table; frame switches back to User1 mode and all dynamic objects 
**	are modified or recreated.  
**	
**	Clicking the Calculate button (available only in Read mode) allows
**	the user to update an entire column of data by applying any 
**	valid expression, such as " * 1.10" (for float, integer, or money) 
**	or "+ '1 day'" (for dates).
**
**	Clicking Close button (landable in all modes) exits the frame.
*******************************************************************************/

initialize () =
declare
  cfield = CompositeField;	/* reference variable pointing to the 
				   FlexibleForm inside the ViewPort */		
  cname = varchar(32) not null;	/* temp variable for column name */
  dexp1 = DynExpr default null;	/* dynamic expression for getting data from
				   a cell in "tbl" */
  dexp2 = DynExpr default null;	/* dynamic expression for setting data of
				   a cell in "tbl" */
  dtc = smallint;		/* temp variable for data type code */
  ef_template = EntryField;	/* object to copy when creating EntryFields */
  efield = EntryField;
  efields = Array of EntryField;/* reference variables pointing to dynamic
				    EntryFields */
  errstr = StringObject;	/* values returned from DynExpr */
  fo = FieldObject default null;/* temp ref var for any field object */
  i = integer not null;       	/* index for various while & for loops */ 	
  mfield = MatrixField default null;	/* the dynamic MatrixField to which 							   fields for search criteria will be 
					   added */
  numsfields = smallint not null;   /* number of fields for search criteria */
  old_table = varchar(32) not null; /* name of the last value for table_choices;
				       blank at startup */
  qi = Qualinfo default null;	/* temp object to hold ClientData */

  qo = QueryObject default null;/* for retrieving the data */
  setup1 = procedure returning integer;  /* creates QueryObject, dynamic 
					    fields for search criteria */ 
  setup2 = procedure returning integer;	 /* adds and removes columns in "tbl" */
  status = integer not null;	/* return status from various methods */
  str = StringObject;		/* string for ReplyPopup answser */
  tarray = Array of Object default null;/* result array for DeclareData method*/
  tname = varchar(32) not null;	/* temp variable for table name */
  trim = Freetrim default null;	/* reference variable for a label */
  trim_template = FreeTrim;	/* object to copy when creating FreeTrim */
  txt = varchar(60) not null;	/* temp var for dynamic expressions, etc. */
  vfield = ViewportField;	/* ref var pointing to the ViewPort */
  wclause = varchar(2000) not null;     /* temp variable for where clause 
					   string */
enddeclare

begin

  /* Set attributes of templates for the fields to be created */

  trim_template.IsBold = TRUE;
  trim_template.TypeSize = 8;
  trim_template.Gravity = FA_CENTERRIGHT;
  ef_template.FocusBehavior = FT_TABTO;
  ef_template.IsBold = TRUE;
  ef_template.TypeSize = 8;
  ef_template.Gravity = FA_CENTERLEFT;
  ef_template.ReadBias = FB_CHANGEABLE;
  ef_template.IsNullable = TRUE;
  ef_template.IsMultiLine = FALSE;
  CurFrame.WindowVisibility = WV_VISIBLE; /* to use data on field size */

  /*  Load up the list of table names in option field; leave first row blank
      so that selecting even the first table will trigger the "on click" 
      event. */

  set autocommit on;
  i = 2;
  select table_name as tname 
  from iitables    
  where table_owner = dbmsinfo('dba')
  order by tname
  begin
    field(table_choices).ValueList.ChoiceItems[i].EnumText = tname;    
    field(table_choices).ValueList.ChoiceItems[i].EnumValue = i;
    i = i + 1;
  end;
  field(table_choices).UpdChoiceList();
  old_table = '';
end

on click table_choices = 
begin

  /*  If table is not same as before, get info about columns in that
      table into column_choices TableField. The TableField is mapped to
      user class ColumnData, because we want info about the datatype
      and nullability for building the table field.  */

  if table_choices != old_table then
    qo = QueryObject.Create();
    qo.Tables[1].TableName = table_choices;
    FIELD(column_choices).Title = 'Columns in table ' + table_choices;
    column_choices.Clear();  
    i = 1;
    select column_name as :column_choices[i].column_name, 
      lowercase(column_datatype) as :column_choices[i].column_datatype, 
      column_length as :column_choices[i].column_length, 
      column_nulls as :column_choices[i].column_nulls 
    from iicolumns 
    where table_name = :table_choices and table_owner = dbmsinfo('dba')
    order by column_name
    begin
      /* For simplicity later, group data types into categories 1 = chars,
	 2 = numeric (float or int), 3 = date, 4 = money. */
      txt = column_choices[i].column_datatype;
      if txt = 'char' or txt = 'varchar' or txt = 'text' or txt = 'c' then 
	column_choices[i].dtc = 1 	/* characters */
      elseif txt = 'float' or txt = 'integer' then	
	column_choices[i].dtc = 2 	/* numeric */
      elseif txt = 'date' then
	column_choices[i].dtc = 3 	
      elseif txt = 'money' then
	column_choices[i].dtc = 4	
      endif;
      /* if not one of the above choices, the column is something like
	 a logical key or user-defined datatype that is undisplayable. */
      if column_choices[i].dtc is not null then /*  acceptable datatype */
        column_choices[i].display_col = TRUE;
        i = i + 1; /* if we don't add 1, we write over col w/undisplayable datatype */
      endif;
    end;

    /* If last row was undisplayable, have to remove it */
    i = column_choices.LastRow;
    if column_choices[i].dtc is null then
      column_choices.RemoveRow(rownumber = i);
    endif;
  endif;
  CurFrame.CurMode = FM_QUERY;
  CurFrame.IsAutoSized = TRUE;
end;

on click go_btn  =
begin 
  if CurFrame.CurMode = FM_QUERY then
    /*  User has selected columns for search criteria and display;
        need to build tablefield & possibly new entry fields. */
    if table_choices != old_table then
      status = callproc setup1(); /* to create fields for search criteria */
      old_table = table_choices;
    else
      status = callproc setup2();  /* to rebuild table field for data display */
    endif;
    if status = ER_OK then      /* No error */
      CurFrame.CurMode = FM_READ;
      if numsfields > 0 then    /* There are fields for search criteria */
	CurFrame.InputFocusField = efields[1];
	resume; 	/* Give user a chance to enter search criteria */
      endif;
    else		/* Error setting up form fields or QueryObject */
      message 'Unable to build table field for displaying data.';
      CurFrame.CurMode = FM_QUERY;
      resume;
    endif;
  endif;

  /*  CurFrame.CurMode must be FM_READ if the program got here. Either user 
      has entered search criteria or there are none; need to step through 
      search criteria fields and create a where clause for the query object, 
      then get the data.  */

  if tarray is not null then
    tarray.Clear();
  endif;
  wclause = '';
  for i = 1 to numsfields do
    efield = efields[i];
    if efield.TextLength > 0 then /* something is entered in this field */	
      if wclause != '' then 
	wclause = wclause + ' and ';
      endif;
      wclause = wclause + Qualinfo(efield.ClientData).String1 
	+ efield.TextValue + Qualinfo(efield.ClientData).String2;
    endif;
  endfor; 
  qo.RunTimeWhere.Value = wclause;
  status = qo.Open(QueryMode = QY_ARRAY, CheckCols = TRUE, 
                    scope = CurFrame.Scope);
  if status != ER_OK then
    message 'Error retrieving data from table ' + table_choices;
    resume;
  endif;
  qo.Close(); 
  if tarray.LastRow = 0 then
    message 'No rows retrieved.';
  else
    field(tbl).UpdField();
  endif;
end

on click newcol_btn =	
begin
  /*  User wants to display different columns of same table; may not change
      search columns at this point. */
  CurFrame.CurMode = FM_QUERY;
  field(column_choices[*].search_col).CurBias = FB_VISIBLE;
  if mfield is not null then
      mfield.SetToDefault();
  endif;
end

on click newtbl_btn =	
begin
  /*  User wants to select a new table for display */
  CurFrame.CurMode = FM_USER1;
  field(table_choices).SetToDefault();
  /*  Set old_table to an impossible name to force calling setup1
      even if user selects same table again (to get different search
      columns, for example. */
  old_table = '123'; 
  field(column_choices[*].search_col).QueryBias = FB_CHANGEABLE;
end

on click calc_btn =
begin
  fo = CurFrame.InputFocusField;
  if fo is null then
    /*  Cursor is not in any field */
    message 'Cursor must be in the table field to calculate';
    resume;
  elseif fo.WhichTableField() is null then   
    /*  it is not in the TableField */
    message 'Cursor must be in the table field to calculate';
    resume;
  elseif EntryField(fo).DataType like 'varchar%' then
    message 'You must be in a numeric, money, or date column to calculate';
    resume
  elseif tarray.LastRow = 0 then
    message 'No rows in the tablefield';
    resume
  endif;

  /*  Prompt for expressison to use and create first dynamic expression.
      "tarray" and "tbl" both point to the array underlying TableField tbl.
      For a DynExpr, when you use the subscript square bracket syntax, the
      array must be declared "array of class", not "array of object".  But
      tarray had to be declared  "array of object" because the class was
      not created until the DeclareData method was invoked.  So here we
      have to use the reference variable "tbl," which was implicitly
      declared as "Typeof(Tbl)", which is aa class.  */

  status = CurFrame.ReplyPopup(messagetext = 
    'Enter expression to be applied to all values in the ' + fo.Name
    + ' column.', reply = str);
  if status = PU_CANCEL then	/*  user has changed mind */
    resume;
  endif;
  txt = 'tbl[i].' + fo.Name + str.Value;
  dexp1 = CurFrame.Scope.CreateDynExpr(string = txt, errors = byref(errstr));
  if errstr is not null then	/* error in creating DynExpr */
    message 'The expression  ''' + txt
      + ''', is not a valid expression; please try again';
    resume;
  endif;

  /* Create second dynamic expression */
  txt = 'tbl[i].' + fo.name;
  dexp2 = CurFrame.Scope.CreateDynExpr(string = txt, errors = byref(errstr));
  if errstr is not null then	/* error in creating DynExpr */
    message 'Error creating second DynExpr';
    resume;
  endif;

  /*  We must use a DynExpr to get or set the values in a dynamically
      declare TableField.  We have used the assign method here to avoid
      the need for an intermediate variable, but it would have worked the
      same had we declared x = float and used the following code:

    	status = dexp1.GetValue(value = byref(x));
    	status = dexp2.SetValue(value = byref(x));  */

  for i = 1 to tarray.LastRow do
    status = dexp2.Assign(FromDynExpr = dexp1);
    if status != ER_OK then
      message 'Error assiigning calculated value in Row ' + varchar(i);
    endif;
  endfor;
end

on click close_btn =
begin
  exit;
end

/***********************************************************************
**  Procedure Setup1
**
**  Description:
**	Called when columns selected for new table.  Populates qo.Columns
**	attributes.   Creates entry fields for search criteria (if any)
**	and adds them to mfield.  Sets numsfields variable to number of 
**	search fields created.  If no errors, calls Setup2.
**
**  Input:  none
**
**  Output:  Returns ER_OK if success, -1 if error
******************************************************************************/

procedure setup1() =
declare
  j = smallint not null;	/* index for search fields  */
  n = smallint not null;	/* temp for data length */
  rnum = smallint not null;	/* row number in MatrixField */
enddeclare
begin
  if mfield is not null then 
    mfield.ParentField = null;
    efields.Clear();
    mfield.ChildFields.Clear();
    mfield = null;
  endif;
  ef_template.OutlineWidth = LW_DEFAULT;

  /*  Step through columns; add every column to the QueryObject, and if column
      is a search col, create form fields with appropriate data type. */

  j = 0; 
  rnum = 0;
  for i = 1 to column_choices.LastRow do
    cname = column_choices[i].column_name;
    qo.Columns[i].ColumnName = cname;
    qo.Columns[i].Targets[1].Expression = cname;
    if column_choices[i].search_col = TRUE then

      /* If this is the first search column, then create the matrix field */
      if j = 0 then
      	mfield = MatrixField.Create(); 
      	mfield.Columns = 4;
      	mfield.Rows = column_choices.LastRow; 
     	mfield.OutlineWidth = LW_NOLINE;
      	mfield.SeparatorWidth = LW_NOLINE;
      	mfield.Ytop = 100;
      	mfield.XLeft = 200; 
      	mfield.User1Bias = FB_INVISIBLE;
      	mfield.User2Bias = FB_INVISIBLE;
      	mfield.QueryBias = FB_INVISIBLE;
      	mfield.ReadBias = FB_CHANGEABLE;
	mfield.ChildBottomMargin = 50;
      endif;
      /*  Create an EntryField and FreeTrim label for entering search 
          criteria.  If date or numeric, create min/max fields so user 
	  can perform a range search.  */  
      j = j + 1;
      rnum = rnum + 1;
      trim = trim_template.Duplicate();
      efields[j] = ef_template.Duplicate();
      qi = Qualinfo.Create();
      dtc = column_choices[i].dtc;
      if dtc = 1 then	 /* string */
        trim.TextValue = uppercase(cname) + ':';
        mfield.InsertChild(fieldtoinsert = trim, row = rnum, column = 1);
        n = column_choices[i].column_length;
        if n > 20 then
	  n = 20;
        endif;
        efields[j].DataType = 'varchar(' + varchar(n) + ')';
	/*  Create character strings necessary for building the query
	    qualification. */
	qi.String1 = cname + ' like ''';
	qi.String2 = '%''';
	efields[j].ClientData = qi.Duplicate();
        efields[j].CharsPerLine = n; 
        mfield.InsertChild(fieldtoinsert = efields[j], row = rnum, column = 2);
      else 	 /* It was date, money, or numeric; need a range search  */
        trim.TextValue = 'MIN ' + uppercase(cname) + ':';
        mfield.InsertChild(fieldtoinsert = trim, row = rnum, column = 1);
        trim = trim_template.Duplicate();
        trim.TextValue = 'MAX ' + uppercase(cname) + ':';
        mfield.InsertChild(fieldtoinsert = trim, row = rnum, column = 3);
        efields[j].CharsPerLine = 15; 
	qi.String1 = cname + ' >= ';
	qi.String2 = '';
	efields[j].DataType = column_choices[i].column_datatype;
	if dtc = 3 then 	/* it was date */
	  efields[j].FormatString = 'd"2/3/01"';
	  qi.String1 = qi.String1 + '''';
	  qi.String2 = '''';
	elseif dtc = 4 then 	/* it was money */
	  efields[j].FormatString = '"$$$$$$$z.nn"';
	  qi.String1 = qi.String1 + '''';
	  qi.String2 = '''';
	endif;
 	efields[j].ClientData = qi.Duplicate();
        mfield.InsertChild(fieldtoinsert = efields[j], row = rnum, column = 2);
	j = j + 1;
	efields[j] = efields[j-1].Duplicate();
        qi = Qualinfo.Create();
	qi.String1 = cname + ' <= ';
	if dtc = 3 then 	/* it was date */
	  qi.String1 = qi.String1 + '''';
	  qi.String2 = '''';
	endif;
 	efields[j].ClientData = qi.Duplicate();
        mfield.InsertChild(fieldtoinsert = efields[j], row = rnum, column = 4);
      endif;
    endif;
  endfor;
  numsfields = j;
  if numsfields > 0 then		/* at least one select criterion */
    mfield.CollapsePolicy = CP_ROWS;	/* get rid of extraneous rows */
    mfield.ParentField = CurFrame.TopForm;	/* add to the form */
  endif;
  status = callproc setup2;
  return status;
end

/***********************************************************************
**  Procedure Setup2
**
**  Description:
**	Deletes old columns from TableField tbl and adds new ones.  Sets
**	some attributes of qo.Columns.  Called by setup1, also by 
**	parentframe when user clicks on new_cols button.  
**
**  Input:  none
**
**  Output:  Returns ER_OK if success, -1 if error, ***********************************************************************/
procedure setup2() =
declare
  n = smallint not null;		/* temp for data length */
  j = smallint not null;  
  ncolumns = smallint not null;
  tfield = TableField;
enddeclare
begin
  CurFrame.CurMode = FM_USER2;	/* everything invisible to prevent flash */
  tfield = field(tbl);
  tfield.Title = 'Columns selected from ' + table_choices;
  tfield.TitleTrim.IsBold = TRUE;
  tfield.TitleTrim.TypeSize = 8;
  if old_table = '' then  
    /*  This is the first time; composite and viewport fields are not named
        (for better performance), so we need to get pointers to them. */
    cfield = tfield.ParentField;  
    vfield = ViewportField(cfield.ParentField);
  endif;
  tfield.ParentField = null;
  qo.TargetArray = NULL;
  ef_template.OutlineWidth = LW_NOLINE;
  if old_table != '' then  /* this is not the first time */
    status = tfield.UndeclareData(); 
    if status != ER_OK then
      message 'Error in UndeclareData method';
      resume; 
    endif;
    tarray = null;
    ncolumns = tfield.TableBody.ChildFields.LastRow;
    for i = ncolumns downto 2 do /* can't delete all columns in a tablefield */
      tfield.DeleteColumn(position = i);
    endfor;
  endif;

  j = numsfields;  /* putting entryfields for table in same array as others */
  ncolumns = 0;
  for i = 1 to  column_choices.LastRow do
    /*  If display_col set for this row, add a column to the tablefield,
        and set qoattribute. */
    if column_choices[i].display_col = TRUE then
      qo.Columns[i].Targets[1].IsSelectTarget = TRUE;
      cname = column_choices[i].column_name;
      dtc = column_choices[i].dtc;
      j = j + 1;
      ncolumns = ncolumns + 1;
      efields[j] = ef_template.Duplicate();
      efields[j].Name = column_choices[i].column_name;
      if column_choices[i].column_nulls = 'Y' then
        efields[j].IsNullable = TRUE;
      else
        efields[j].IsNullable = FALSE;
      endif;
      if dtc = 1 then 		/* it is a string */
        n = column_choices[i].column_length;
        if n > 20 then
	  n = 20;
        endif;
        efields[j].DataType = 'varchar(' + varchar(n) + ')';
      elseif dtc = 2 then	/* it is float or integer */
	efields[j].DataType = column_choices[i].column_datatype;
	n = 10;
      elseif dtc = 3 then 	/* it is date */
	efields[j].DataType = 'date';
	efields[j].FormatString = 'd"2/3/01"';
	n = 8;
      elseif dtc = 4 then 	/* it is money */
	efields[j].DataType = 'money';
	efields[j].FormatString = '"$$,$$$,$$n.nn"';
        n = 13;
      endif;
      efields[j].CharsPerLine = n; 
      tfield.InsertColumn(fieldtoinsert = efields[j], position = ncolumns,
        title = uppercase(cname));
        columnfield(tfield.TableBody.ChildFields[ncolumns]).TitleTrim.IsBold 
	  = TRUE; 
        columnfield(tfield.TableBody.ChildFields[ncolumns]).TitleTrim.TypeSize
	  = 8; 
    else	/* don't display this column */
      qo.Columns[i].Targets[1].IsSelectTarget = FALSE;
    endif
  endfor;

  /* Since it wouldn't delete the last column, you have to do it at the end */

  tfield.DeleteColumn(position = ncolumns+1);
  if ncolumns = 0 then
    message 'You must specify at least one display column.';
    return -1;
  elseif numsfields > 0 then	/* there are fields for search criteria */
    n = mfield.AbsYBottom + 50;
    tfield.NumVisibleRows = 8;
  else				/* no fields for search criteria */
    n = 50;
    tfield.NumVisibleRows = 12;
  endif;
  tfield.ParentField = CurFrame.TopForm;  /* can't get object size until
					     it has a parent */
  if tfield.Width > (CurFrame.WindowWidth - 250) then 
    /* tbl wider than window; put it in the viewport */
    tfield.ParentField = cfield;
    vfield.ReadBias = FB_LANDABLE;
    vfield.YTop = n;
    vfield.Height = tfield.Height + 100;
  else		/* ignore viewport, center it horizontally in the form */
    tfield.YTop = n;
    tfield.XLeft = (CurFrame.WindowWidth - tfield.Width - 500) / 2;
    vfield.ReadBias = FB_INVISIBLE;
  endif;
  CurFrame.CurMode = FM_READ;	
  CurFrame.IsAutoSized = TRUE;

  /*  The result for a DeclareData method must refer to an array that has
      been declared.  But "tbl" isn't a declared array until after the
      DeclareData method has been invoked successfully.  */
  status = tfield.DeclareData(result = byref(tarray)); 
  if status = ER_OK then
    qo.TargetArray = tarray;
    return ER_OK;
  else
    return -1;
  endif;
end

row.script:
on click =
declare
    table_field = TableField default NULL;
    tf_array	= Array of Object default NULL;
enddeclare
begin
    table_field = CurFrame.TriggerField.WhichTableField();
    table_field.GetFieldValue(value = byref(tf_array));
    if table_field.ActiveRow = 0 then
	message _StringSub(string = _C_SelectRowOfTblFldBefore,
                             arg1 = _C_InsertBeforeCurrentRow,
                             arg2 = _C_inserted);
	resume;
    endif;

    tf_array.InsertRow(rownumber = table_field.ActiveRow);
    table_field.HasDataChanged = TRUE;

    /* Set state of new row so it will become RS_NEW when user types on it.
    ** (InsertRow rowstate defaults to RS_UNCHANGED)
    */
    tf_array[table_field.CurRow - 1]._Rowstate = RS_UNDEFINED;

    /*
    ** Note that setting ActiveRow has no immediate effect.
    ** Instead, it causes an internal event to be queued to process any
    ** SetValue and Exit events as the focus attempts to move.
    */
    table_field.ActiveRow = table_field.CurRow - 1;
end

on click =
declare
    table_field = TableField default NULL;
    tf_array	= Array of Object default NULL;
enddeclare
begin
    table_field = CurFrame.TriggerField.WhichTableField();
    table_field.GetFieldValue(value = byref(tf_array));
    if table_field.ActiveRow = 0 then
	message _StringSub(string = _C_SelectRowOfTblFldBefore,
			     arg1 = _C_DeleteCurrentRow,
			     arg2 = _C_deleted);
	resume;
    endif;
    tf_array.SetRowDeleted(rownumber = table_field.ActiveRow);
    table_field.HasDataChanged = TRUE;
end

on click =
declare
    table_field = TableField default NULL;
    tf_array	= Array of Object default NULL;
    i		= integer not null;
enddeclare
begin
    table_field = CurFrame.TriggerField.WhichTableField();
    table_field.GetFieldValue(value = byref(tf_array));
    /* Delete each row, rather than tf_array.Clear(), so rows get
    ** correct _RowState.
    */
    for i = tf_array.LastRow downto 1 do
	tf_array.SetRowDeleted(rownumber = i);
    endfor;
    table_field.HasDataChanged = TRUE;
end

COMPONENT.script:
/* Methods for EMPLOYEE class */

/*****************************************************************************
**  Hire method adds a new employee by getting the next EMP_NUM and inserting
**  rows in the V_EMP and V_JobHistory tables.  Method is private, since every  
**  employee must be of a specific subclass of employee, and hire method needs
**  to be slightly different for each. 
**
**  History:
**	12-oct-95(timt) - use 'order by 3' instead of 'order by
**			  sdate' since CA-INGRES DESKTOP does not support
**			  as name in order by clause.
**
**	20-oct-95(timt) - change column name from 'number' to 'h_number'
******************************************************************************/

method Hire() =
declare
  errstat = integer not null;
enddeclare

begin
  if CurObject.HireDate = ' ' then 
    CurObject.HireDate = 'today'; 
  endif;
  update v_highest_number 
  set h_number = h_number + 1 where tbl_name = 'v_emp';
  select h_number as :CurObject.EmpNum from v_highest_number
    where tbl_name = 'v_emp';
  insert into v_emp (emp_num, emp_type, last_name, first_name, hire_date,
    term_date, term_reason) 
  values (:CurObject.EmpNum, :CurObject.Type, :CurObject.LastName, 
    :CurObject.FirstName, :CurObject.HireDate, :CurObject.TerminationDate,
    :CurObject.TerminationReason);
  if CurMethod.DBSession.ErrorNumber = 0 then
    insert into v_jobs (emp_num, job_class, pay, start_date)
    values (:CurObject.EmpNum, :CurObject.CurrentClass, :CurObject.CurrentPay, 
      :CurObject.HireDate);
  endif;
  if CurMethod.DBSession.ErrorNumber != 0 then
    rollback;
    message 'Database error; unable to insert employee record.';
  endif;
  return CurMethod.DBSession.ErrorNumber;
end

/*****************************************************************************
**  Terminate method is same for all employees 
*****************************************************************************/

method Terminate() =
declare
  errstat = integer not null;
  str = StringObject;
enddeclare

begin
  if CurObject.TerminationDate = ' ' then 
    CurObject.TerminationDate = 'today';
  endif;
  str.ConcatVarchar(text = CurObject.TerminationReason);
  errstat = CurMethod.ReplyPopup(messagetext = 'Enter reason for termination:',
    reply = str);
  if errstat = PU_OK then
    update v_emp set term_date = :CurObject.TerminationDate,
      term_reason = :str.Value 
    where emp_num = :CurObject.EmpNum;
    errstat = CurMethod.DBSession.ErrorNumber;
    if errstat = 0 then
      update v_jobs set end_date = :CurObject.TerminationDate
      where emp_num = :CurObject.EmpNum and end_date is null;
      errstat = CurMethod.DBSession.ErrorNumber;
    endif;
    if errstat = 0 then
      CurMethod.InfoPopup(messagetext = 'Employee #' + CurObject.EmpNum
        + 'successfully terminated as of ' 
	+ varchar(CurObject.TerminationDate));
    else
      rollback;
      CurMethod.InfoPopup(messagetext = 'Database error - employee #' 
	  + CurObject.EmpNum + 'could not be terminated.', 
	  messagetype = MT_ERROR); 
    endif;
  endif;
  return errstat;
end

/*****************************************************************************
**  Method GetHistory called for all subclasses to fill JobHistory attribute 
*****************************************************************************/

method GetHistory() =
declare
  i = smallint not null;
  njob = smallint not null;
  errstat = integer not null;
  sdate = date not null;
enddeclare

begin
  i = 1;
  njob = 0;
  select j.pay  as :CurObject.JobHistory[i].Pay, 
    j.job_class as :CurObject.JobHistory[i].Class, 
    j.start_date as :sdate, 
    j.end_date as :CurObject.JobHistory[i].EndDate, 
    c.job_title as :CurObject.JobHistory[i].Title
  from v_jobs j, v_job_classes c
  where j.emp_num = :CurObject.EmpNum and j.job_class = c.job_class
  order by 3 desc
    begin
      if njob = 0 then
        CurObject.CurrentPay = CurObject.JobHistory[i].Pay;
	CurObject.CurrentClass = CurObject.JobHistory[i].Class;
	CurObject.CurrentTitle = CurObject.JobHistory[i].Title;
      else
    	CurObject.JobHistory[i].StartDate = sdate;
        i = i + 1;
      endif;
      njob = njob + 1;
    end;
  errstat = CurMethod.DBSession.ErrorNumber;
  if errstat != 0  then	
    message 'Database error ' + varchar(errstat) 
      + '; cannot get employee data.';
  else
    if njob = 1 then
      /* Employee has had only one job; same data in current & history. */
      CurObject.JobHistory.Clear();
      CurObject.JobHistory[1].Title = '*** No History ***';
    endif;
  endif;
  return errstat;
end

/*****************************************************************************
**  For the abstract user class Employee, method GetCurrent merely gets info 
**  from v_emp.  Returns 0 if succes, -1 if employee does not exist,else 
**  error number. 
*****************************************************************************/

method GetCurrent() =
declare
  edate = date;
  errstat = integer not null;
enddeclare

begin
  select emp_type as :CurObject.Type,
    last_name as :CurObject.LastName, 
    first_name as :CurObject.FirstName, 
    hire_date as :CurObject.HireDate, 
    term_date as :CurObject.TerminationDate, 
    term_reason as :CurObject.TerminationReason 
  from v_emp 
  where emp_num = :CurObject.EmpNum; 
  errstat = CurMethod.DBSession.ErrorNumber;
  if errstat != 0 then;
    message 'Database error ' + varchar(errstat) 
      + '; cannot get employee data.';
  elseif CurMethod.DBSession.RowCount = 0 then
    errstat = -1;
    message 'No employee #' + varchar(CurObject.EmpNum);
  endif;
  return errstat;
end

/* ** Name: error_handler - Detects and handle DBMS errors. ** ** Description: ** This procedure first checks if there an error occurred in the ** previous DBMS statement. If so, it pops up an error message. ** If error was a deadlock or other error where retry is ** appropriate; if it was, an event is sent back to the calling frame ** to retry the DBMS statement. ** ** No need to roll back transaction in either case, because in the ** main_control frame we set the session to automatically roll back ** the transaction on ANY error. ** ** Called By: ** check_out frame. ** ** Parameters: ** frm Frame which called this procedure. ** commit_evt Event to send frame if we need to retry DBMS statements ** if there is a deadlock. ** ** Returns: ** DBSession.ErrorNumber set by previous DBMS statement; ER_OK (0) if success */
procedure error_handler ( frm = FrameExec, /* Calling frame */ commit_evt = varchar(32), /* Commit event for retries if ** a deadlock occurred. */ ) = declare err_no = integer not null /* Error number */ enddeclare
begin err_no = CurProcedure.DBSession.ErrorNumber; if err_no = 49000 then /* We have encountered a deadlock or log full. */ message 'Deadlock error occurred while updating changes in the database.'; frm.SendUserEvent(commit_evt); elseif err_no != ER_OK then message 'Error occurred while making changes in the database. ' + dbms_error_message(); endif; return err_no; end;

/*
**  Name:	find_string
**
**  Description:
**	This frame prompts for the string and looks for it in the given array
**	in the given column.  If it is found the row number is returned.
**
**  Input:
**	title	        String for WindowTitle of child frame
**	array_name	Array to search for string
**	starting_row	What row of array to start searching
**	column_name	Name of column to search
**	column_title	Title of column to search
**	search_string	String to search for (form field - not declared)
**	ignore_case	Ignore case in search (form field - not declared)
**	match		Type of matching (form field - not declared)
**			  1 = Match first characters.
**			  2 =   "    any      "
**			  3 =   "   last      "
**			  4 =   "    all      "
**
**  Returns:
**	The row number containing the found string.
**	0 if Cancel button pressed.
**
*/

initialize(
  array_name = Array of Object default null,
  starting_row = integer not null,
  column_name = varchar(32) not null,
  column_title = varchar(33) not null,
  title = varchar(100) not null
) =
declare
  modified_search_string = varchar(102) not null;
  case_sensitive = smallint not null;
  row_number = integer not null,
  status = integer not null;
enddeclare

begin
  CurFrame.WindowTitle = title;
  field(search_string_title).TextValue = column_title + ':';
end

on click find_btn =
begin
  status_msg = '';
  if length(search_string) = 0 then
    status_msg = 'Enter ' + lowercase(column_title) + ' to find.';
    curframe.Beep();
    field(search_string).MarkAllText();
    resume;
  endif;

  if match = 1 then
    modified_search_string = search_string + '%';
  elseif match = 2 then
    modified_search_string = '%' + search_string + '%';
  elseif match = 3 then
    modified_search_string = '%' + search_string;
  elseif match = 4 then
    modified_search_string = search_string;
  endif;

  if ignore_case = TRUE then
    case_sensitive = FALSE;
  else
    case_sensitive = TRUE;
  endif;

  status = array_name.Find(AttributeName = column_name,
    SearchString = modified_search_string,
    CaseSensitive = case_sensitive,
    StartRow = starting_row,
    EndRow = starting_row - 1,
    RowNumber = byref(row_number));

  if status = ER_FAIL or status = ER_ROWNOTFOUND then   
    status_msg = 'Cannot find ' + lowercase(column_title) + '.';
    CurFrame.Beep();
    field(search_string).MarkAllText();
  else
    return row_number;
  endif;
end

on windowclose,
on click cancel_btn =
begin
  return 0;
end

row.script:
ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	IF table_field.ActiveRow = 0 THEN
		MESSAGE 'You must select a row of the table field ' +
			'to insert a row before it.';
		RESUME;
	ENDIF;
	tf_array.InsertRow(rownumber = table_field.ActiveRow);
	table_field.HasDataChanged = TRUE;
	table_field.ActiveRow = table_field.CurRow - 1;
	tf_array[table_field.ActiveRow]._Rowstate = RS_UNDEFINED;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	IF table_field.ActiveRow = 0 THEN
		MESSAGE 'You must select a row of the table field ' +
			'to delete it.';
		RESUME;
	ENDIF;
	tf_array.SetRowDeleted(rownumber = table_field.ActiveRow);
	table_field.HasDataChanged = TRUE;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	tf_array.Clear();
	table_field.HasDataChanged = TRUE;
END

COMPONENT.script:
method CheckJob() = 
declare
  errstat = integer not null;
  nrows = smallint not null;
enddeclare

begin
  select job_title as :CurObject.Title, 
    min_pay as :CurObject.MinPay, 
    max_pay as :CurObject.MaxPay
  from v_job_classes
  where job_class = :CurObject.Class and job_type = :CurObject.Type;
  nrows = CurMethod.DBSession.RowCount;
  return nrows;
end

/*
** Name: main_control	- Starting frame for application.
**
** Description:
**	Display a list of options as a start up frame.  This is
**	displayed in a table field, called "selection_table".
**	This is filled in with the number of frames that can
**	be called.  This list is filled in in the initialized
**	block.  To add another option, simply add another entry 
**	to the table.  The frame will adjust it's size to match.
**
** Parameters:
**	(None)  Used as top frame of application.
**
** Returns:
**	(None)  Used as top frame of application.
** History:
**	19-oct-1995 (timt)
**	    DBI Change.  Remove '(Re-)Create Data Tables' menu item 
**	    since tables/view can be created through demobuilder.
*/

initialize =
declare
  handlerproc = procedure returning integer not null;   /* local procedure */
enddeclare

begin
  /* Establish "handlerproc" as the procedure to handle data-entry
  ** (i.e. frontend) errors for this frame and all its child frames
  ** (unless superseded by procedures specified in child frames). */
  CurFrame.DataEntryErrorHandler = CurFrame.Scope.GetProcHandle
    (name = 'handlerproc');

  /* Fill in the table field with the frame names, etc. */
  selection_table[1].frame_name = 'video_list';
  selection_table[1].frame_description = 'Video List';

  selection_table[2].frame_name = 'check_out';
  selection_table[2].frame_description = 'Check Out/In Videos';

  selection_table[3].frame_name = 'personnel';
  selection_table[3].frame_description = 'Employee Actions';

  selection_table[4].frame_name = 'customer_maintenance';
  selection_table[4].frame_description = 'Customer Maintenance';

  selection_table[5].frame_name = 'customer_browse';
  selection_table[5].frame_description = 'Browse Customers';

  selection_table[6].frame_name = 'customer_maintenance2';
  selection_table[6].frame_description = 'Alternative Customer Maintenance';

  selection_table[7].frame_name = 'sales_barchart';
  selection_table[7].frame_description = 'Sales Charts';

  /* Adjust the size of the table to match */
  field(selection_table).NumVisibleRows = selection_table.LastRow;

end

on click go_button =
begin
  /* The name of the frame to call is in the "frame_name" hidden column */
  callframe :selection_table[].frame_name;
end

on click close_button,
on windowclose =
begin
  exit;
end

/****************************************************************************
**
**  Local procedure handlerproc
**
**  Description:  Handles data-entry errors; i.e. errors that occur when user
**	enters characters into a numeric field, enters an invalid date in a 
**	date field, etc.  
**
**	Beep no matter what data type "errorfield" has.  If "errorfield" is
**	a date field, display message about entering a valid date.  For
**	other datatypes, display no error message. 
**
**  Called by:  implicitly (not explicitly) by this frame or, by default, 
**	any of its child frames
**
**  Input:  errorfield		the EntryField on which the error occurred
**	    errorframe		frame in which the error occurred
**	    errormessage	errormessage that will be displayed in a
**				popup after return from this procedure
**
**  Output:  EH_RESUME		resume normal execution after return (after
**				popup display of error message, if any)
******************************************************************************/

procedure handlerproc(
  errorfield = entryfield;
  errorframe = frameexec;
  errormessage = varchar(2000) not null) =

begin
  CurProcedure.beep();
  if errorfield.DataType = 'date' then
    errormessage = 'You must enter a valid date in this field';
  else
    errormessage = '';
  endif;
  return EH_RESUME;
end

