COMPONENT.script:
/***************************************************************************
**   Hire method for managers.  These are same as permanent employees but get
**   bonuses.  Hire method is the same, but we need to supply the BonusRate 
**   in CurObject.BonusTemp so that can be stored also.
***************************************************************************/

method Hire() =
declare
  errstat = integer not null;
enddeclare

begin
  CurObject.BonusTemp = CurObject.BonusRate;
  errstat = CurMethod.SendSuperclass();
  return errstat;
end    

/***************************************************************************
**  GetCurrent method is the same as for permanent employees except that
**  we need the attribute BonusRate that PermanentEmployee.GetCurrent()
**  put in the private attribute BonusTemp. 
***************************************************************************/

method GetCurrent() =
declare
  errstat = integer not null;
enddeclare

begin
  errstat = CurMethod.SendSuperclass();
  if errstat = 0 then
    CurObject.BonusRate = CurObject.BonusTemp;
  endif;
  return errstat;
end

/***************************************************************************
**  GetHistory method is the same as for permanent employees except that
**  we need the attribute BonusRate that PermanentEmployee.GetHistory()
**  put in the private attribute BonusTemp.
***************************************************************************/

method GetHistory() =
declare
  errstat = integer not null;
enddeclare

begin
  errstat = CurMethod.SendSuperclass();
  if errstat = 0 then
    CurObject.BonusRate = CurObject.BonusTemp;
  endif;
  return errstat;
end

/***************************************************************************
**  Hire method for permanent employees.  These are paid a monthly salary,  
**  get vacation and insurance, do not get bonuses.
**
**  History:
**	12-oct-95(timt) - use 'order by 11' instead of 'order by edate'
**		      	  since CA-INGRES DESKTOP does not support as
**			  name in order by clause.
***************************************************************************/

method Hire() =
declare
  errstat = integer not null;
enddeclare

begin
  errstat = CurMethod.SendSuperclass();
  if errstat = 0 then
    CurObject.NextReviewDate = CurObject.HireDate + '3 months';
    insert into  v_permemp (emp_num, next_review_date, insurance_plan, 
      bonus_rate) 
    values (:CurObject.EmpNum, :CurObject.NextReviewDate, 
      :CurObject.InsurancePlan, :CurObject.BonusTemp);
    errstat = CurMethod.DBSession.ErrorNumber;
    if errstat != 0 then
      rollback;
      CurMethod.InfoPopup(messagetext = 'Database insert failed with error '
  	+ varchar(CurMethod.DBSession.ErrorNumber), message_type = MT_ERROR);
    endif;
  endif;
  return errstat;
end   

/***************************************************************************
**  Method GetCurrent gets current job info for a permanent employee.
**  Returns 0 if succes, -1 if employee does not exist or is not
**  permanent, else error number.
***************************************************************************/

method GetCurrent() =
declare
  edate = date;
  errstat = integer not null;
enddeclare

begin
  select e.last_name as :CurObject.LastName, 
    e.first_name as :CurObject.firstName, 
    e.hire_date as :CurObject.HireDate, 
    e.term_date as :CurObject.TerminationDate, 
    e.term_reason as :CurObject.TerminationReason,
    p.next_review_date as :CurObject.NextReviewDate, 
    p.accrued_vacation as :CurObject.VacationDays,
    p.last_review_result as :CurObject.LastReviewResult, 
    p.insurance_plan as :CurObject.InsurancePlan,
    p.bonus_rate as :CurObject.BonusTemp, 
    j.end_date as :edate,
    j.pay as :CurObject.CurrentPay, 
    j.job_class as :CurObject.CurrentClass, 
    c.job_title as :CurObject.CurrentTitle
  from v_emp e, v_permemp p, v_jobs j, v_job_classes c
  where e.emp_num = :CurObject.EmpNum and e.emp_type = :CurObject.Type
    and p.emp_num = e.emp_num and j.emp_num = e.emp_num
    and c.job_type = :CurObject.Type and c.job_class = j.job_class
  order by 11 desc
    begin
      endloop; /* get only the last job */
    end; 
  errstat = CurMethod.DBSession.ErrorNumber;
  if errstat != 0 then;
    message 'Database error ' + varchar(errstat) 
      + '; cannot get employee data.';
  elseif CurMethod.DBSession.RowCount = 0 then
    errstat = -1;
    message 'No permanent employee #' + varchar(CurObject.EmpNum);
    errstat = -1;
  endif;
  return errstat;
end

/***************************************************************************
**  Method GetHistory gets complete job history for a permanent employee.
***************************************************************************/

method GetHistory() =
declare
  errstat = integer not null;
enddeclare

begin
  select e.last_name as :CurObject.LastName, 
    e.first_name as :CurObject.firstName, 
    e.hire_date as :CurObject.HireDate, 
    e.term_date as :CurObject.TerminationDate, 
    e.term_reason as :CurObject.TerminationReason,
    p.next_review_date as :CurObject.NextReviewDate, 
    p.accrued_vacation as :CurObject.VacationDays,
    p.last_review_result as :CurObject.LastReviewResult, 
    p.insurance_plan as :CurObject.InsurancePlan,
    p.bonus_rate as :CurObject.BonusTemp
  from v_emp e, v_permemp p
  where e.emp_num = :CurObject.EmpNum and e.emp_type = :CurObject.Type
    and p.emp_num = e.emp_num;
  errstat = CurMethod.DBSession.ErrorNumber;
  if errstat != 0 then;
    message 'Database error ' + varchar(errstat) 
      + '; cannot get employee data.';
  else
    if CurMethod.DBSession.RowCount = 0 then
      message 'No permanent employee #' + varchar(CurObject.EmpNum);
      errstat = -1;
    else
      errstat = CurMethod.SendSuperclass();
    endif;
  endif;
  return errstat;
end

/****************************************************************************
**  Frame PERSONNEL - demonstrates user class methods, object-oriented style
**
**  Description:  
**	Frame for performing standard personnel actions represented by a valid 
**	user class that is a descendent of the Employee user class.  
**
**	When the user chooses the class (temp, permanent, or manager), a
**	stackfield mapped to the appropriate class is moved to the top
**	layer of the form, and Emp is created as an object of the
**	appropriate class.  Thus when a method such as "Hire" is invoked 
**	on Emp, the method for the correct subclass is executed.
**
**	Because this frame was developed in an "object-oriented" style,
**	much of the code is found in individual fields scripts rather than
**	in the frame script.
**
**  Parameters:  None
**
**  Return value:  None
****************************************************************************/

initialize() =
declare
  emp = Employee default null;
  empfield = CompositeField;
  errstat = integer not null;
  jclass = JobClass;
  get_empdata = procedure returning none;
enddeclare

begin
  field(temp.jobhistory).RowSeparatorWidth = LW_NOLINE;
  field(memp.jobhistory).RowSeparatorWidth = LW_NOLINE;
  field(pemp.jobhistory).RowSeparatorWidth = LW_NOLINE;

  emp = pemp;
  empfield = field(pemp);
  empfield.LayerSequence = 3;
end

on childsetvalue temp, 
on childsetvalue pemp,
on childsetvalue memp =
begin
  if CurFrame.TriggerField.Name = 'empnum' then 
    if emp.empnum > 0 then
      callproc get_empdata();
    endif;
  elseif CurFrame.TriggerField.Name = 'currentclass' then 
    if jclass.Class != emp.CurrentClass then
      jclass.Class = emp.CurrentClass;
      jclass.Type = emp.Type;
      if jclass.CheckJob() = 0 then
        message 'Not a valid job class for ' + field(emp_type).CurEnumText
  	  + ' employees';
        resume; 
      endif; 
    endif;
    emp.CurrentTitle = jclass.Title;
    empfield.UpdField();
  elseif CurFrame.TriggerField.Name = 'currentpay' then 
    if emp.currentpay < jclass.minpay then
      CurFrame.InfoPopup(messagetext = 'Pay is too low for this job class.'
        + '  Must be in range ' + varchar(jclass.minpay) + ' to '
	+ varchar(jclass.maxpay), messagetype = MT_ERROR);
      resume;
    elseif emp.currentpay > jclass.maxpay then
      CurFrame.InfoPopup(messagetext = 'Pay is too high for this job class.'
        + '  Must be in range ' + varchar(jclass.minpay) + ' to '
	+ varchar(jclass.maxpay), messagetype = MT_ERROR);
      resume;
    endif;
  endif;
end

procedure get_empdata() =
begin
  if display_type = 1 then	/* get current job info only */
    errstat = emp.GetCurrent();
  else
    errstat = emp.GetHistory();
  endif;
  if errstat = 0 then 	/* data row found */
    empfield.UpdField();
  endif;
end;

row.script:
on setvalue =
begin
  if display_type = 1 then
    CurFrame.CurMode = FM_UPDATE;
  else
    CurFrame.CurMode = FM_USER1;
  endif;
  CurFrame.IsAutoSized = TRUE;
end

declare
  str = StringObject;
  n = integer not null;
  enum = integer not null;
enddeclare

on SetValue =
begin
  empfield.SetToDefault();
  if emp_type = 4 then
    if CurFrame.ReplyPopup(messagetext = 'Enter Employee Number:',
    reply = str) = PU_CANCEL then
      resume;
    else   
      emp = Employee.Create();
      enum = int4(str.Value);
      emp.EmpNum = enum;
      n = emp.GetCurrent();
      commit;
      if n != 0 then	/* error or employee not found */
	resume;
      elseif emp.Type = 'p' then
        emp_type = 1;
      elseif emp.Type = 't' then
        emp_type = 2;
      elseif emp.Type = 'm' then
        emp_type = 3;
      else
	message 'Employee #' + str.Value + ' is of type ' + emp.Type
	  + '. This frame cannot deal with that.';
	resume;
      endif;
    endif;
  endif;

  if emp_type = 1 then
    emp = PermanentEmployee.Create();
    emp = pemp;
    emp.Type = 'p';
    empfield = field(pemp);
  elseif emp_type = 2 then
    emp = TempEmployee.Create();
    emp = temp;
    emp.Type = 't';
    empfield = field(temp);
  elseif emp_type = 3 then
    emp = Manager.Create();
    emp = memp; 
    emp.Type = 'm';
    empfield = field(memp);
  endif;

  /*  Move the matrix field we want to work with the the top layer, 
      hiding the others. */
  empfield.LayerSequence = 3;

  /*  If emp_type was previously "don't know", get remaining info for
      that employee. */

  if enum > 0 then
    emp.EmpNum = enum;
    callproc get_empdata(); 
  endif;
end;

on click =
begin
  emp.Terminate();
  commit;
end

on click =
begin
  /* Be sure user has filled in all required fields. If not, set input
     focus to the first field with missing data. */
  if empfield.CheckFailedMandatory(SetFocusOnFailure = TRUE) is not null then;
  elseif emp.CurrentPay = 0.00 then
    CurFrame.InfoPopup(messagetext = 'You must enter a pay rate.',
       messagetype = MT_ERROR);
  else
  /* Invoke Hire method to do the work. */ 
    errstat = emp.Hire();
    if errstat = 0 then 
      commit;
      message emp.FirstName + ' ' + emp.LastName 
        + ' hired as  employee #' + varchar(emp.EmpNum);
    endif;
  endif;
end

