COMPONENT.script:
/*
** Name: sales_linechart  - Show line chart of sales per quarter.
**
** Description:
**	This displays a line chart, dynamically constructed, for the sales
**	for the last two years worth of quarters.  It connects the points
**	for two consecutive quarters, and puts a little rectangle on the
**	point.
**
**	It also allows you to transfer to a bar chart form of
**	the same data, or close back to the main_control frame.
**
** Called By:
**	sales_barchart
**
** Parameters:
**	sales_array	array of saleschart_data
**
** Returns:
**	(None)
*/

initialize(sales_array = array of saleschart_data) =
declare
  i = integer not null;			/* index to array */
  ymax = integer not null;		/* max height of Y increment */
  xmax = integer not null;		/* max width of graph */
  nextsegment = SegmentShape with default null;	/* next segment to display */
  endpoint = RectangleShape with default null;	/* point on the graph */
  nextquarter = FreeTrim;		/* next quarter label to display */
  nextsales_value = EntryField with default null;  /* next value label */
  labelheight = integer not null;	/* height of a character in label */
  labelstart = integer not null;	/* last X point of labels */
  lastx = integer not null;		/* last X point of segment */
  newx = integer not null;		/* new X point of segment */
  point_size = integer not null;	/* Size of rectangle for end point */
  segmentheight = integer not null;	/* height of segments */
enddeclare

begin

  /* Assign values to some variables */

  nextquarter.IsBold = TRUE;
  nextquarter.TypeSize = 8;
  nextquarter.TextValue = 'A';  /* to get the height of typical character */
  labelheight = nextquarter.Height + 40;
  point_size = 50;
  ymax = field(linechart_subform).Height;
  labelstart = 0;
  lastx = 0;

  /* You need to get the form displayed first, to "kick in" much of
  ** the automatic creation of widgets associated with the dynamic
  ** field creation in the initialize section.  If you don't do this,
  ** some of the operations which query the objects that are dynamically
  ** created (such as getting the nextquarter.Width value) will not
  ** be correct.  By setting WindowVisibility, the window and form
  ** are displayed, and widgets are created as they are attached to
  ** a parent. */

  CurFrame.WindowVisibility = WV_VISIBLE;

  /* Scale all the values for the chart, storing results in sales_array.y */
  callproc scale_y_array(low_value = 0.0, high_value = 1000.0,
    low_y_point = ymax - (labelheight*2), high_y_point = 0, 
    sales_array = sales_array);

  /* Loop through the data array and draw the text and lines */

  for i = 1 to sales_array.LastRow do

    /* Add the quarter name label */
    nextquarter = FreeTrim.Create();
    nextquarter.XLeft = labelstart;
    nextquarter.YTop = ymax - labelheight + segmentheight;
    nextquarter.TextValue = shift(varchar(sales_array[i].quarter),-3); 
    nextquarter.IsBold = TRUE;
    nextquarter.TypeSize = 8;
    nextquarter.CharsPerLine = 6;
    nextquarter.OutlineWidth = LW_NOLINE;
    nextquarter.IsMultiLine = FALSE;
    nextquarter.BGcolor = CurFrame.BGcolor;
    nextquarter.ParentField = field(linechart_subform);

    /* Add the value as a label */
    nextsales_value = EntryField.Create();
    nextsales_value.XLeft = labelstart;
    nextsales_value.YTop = ymax - (labelheight*2);
    nextsales_value.DataType = 'money';
    nextsales_value.FormatString = '"$$$,$$$"';
    nextsales_value.UpdateBias = FB_VISIBLE;
    nextsales_value.IsBold = TRUE;
    nextsales_value.TypeSize = 8;
    nextsales_value.OutlineWidth = LW_NOLINE;
    nextsales_value.SetFieldValue(value = sales_array[i].sales_value);
    nextsales_value.CharsPerLine = 7;
    nextquarter.BGcolor = CurFrame.BGcolor;
    nextsales_value.ParentField = field(linechart_subform);

    /* Get the scaled X value for this point */
    newx = labelstart + nextquarter.Width/2;

    /* Now add the line segment for the line graph.  This will only be
    ** added for the second and subsequent subsequent values, as it is
    ** a connection between two points and the first value would not 
    ** have a connecting point */
    if i > 1 then	/* connect points after the 1st point */
      nextsegment = SegmentShape.Create();	
      nextsegment.SetEndPoints(Point1X = lastx, Point1Y = sales_array[i-1].y,
        Point2X = newx, Point2Y = sales_array[i].y);
      nextsegment.LineWidth = LW_VERYTHIN;
      nextsegment.ParentField = field(linechart_subform);
    endif;

    /* Now put in the rectangle centered at the end point */
    endpoint = RectangleShape.Create();
    endpoint.XLeft = newx - point_size;
    endpoint.YTop = sales_array[i].y - point_size;
    endpoint.Width = 2*point_size;
    endpoint.Height = 2*point_size;
    if CurSession.DisplayCapability = DC_COLOR then
      endpoint.FillColor = CC_BLUE;
    else
      endpoint.FillColor = CC_FOREGROUND;
    endif;
    endpoint.FillPattern = FP_SOLID;
    endpoint.LineWidth = LW_NOLINE;
    endpoint.ParentField = field(linechart_subform);

    /* Save this point for the next line segment */
    lastx = newx;

    /* Move the lines over twice the width of the label for next label.*/
    labelstart = labelstart + nextquarter.Width*2;
   endfor;

  /* All lines have been created.  Now resize the subform to fit
  ** data correctly */
  field(linechart_subform).Width = labelstart - nextquarter.Width;

  /* Now that you know how wide the subform is, dynamically add some 
  **  segments to the subform to separate the two labels from the graph */
  xmax = field(linechart_subform).Width;
  nextsegment = SegmentShape.Create();
  nextsegment.SetEndPoints(
    Point1X = 0,
    Point1Y = ymax - labelheight,
    Point2X = xmax,
    Point2Y = ymax - labelheight);
  nextsegment.LineWidth = LW_MINIMUM;
  nextsegment.ParentField = field(linechart_subform);

  segmentheight = nextsegment.Height;

  nextsegment = SegmentShape.Create();
  nextsegment.SetEndPoints(
    Point1X = 0,
    Point1Y = ymax - (labelheight*2) - segmentheight,
    Point2X = xmax,
    Point2Y = ymax - (labelheight*2) - segmentheight);
  nextsegment.LineWidth = LW_MINIMUM;
  nextsegment.ParentField = field(linechart_subform);
end

on click goto_barchart_button =
begin
  gotoframe sales_barchart;
end

on click close_button,
on windowclose =
begin
  return;
end

