COMPONENT.script:
/***************************************************************************
**  Hire method for Temporary employees.  These are paid hourly, get no
**  vacation or insurance, have a termination date specified at hire time.
**
**  History: 
**    12-oct-95(timt)- use 'order by 6' instead of 'order by edate'
**		       since CA-INGRES DESKTOP does not support as name
**		       in order by clause.
***************************************************************************/

method Hire() =
declare
  errstat = integer not null;
enddeclare

begin
  if CurObject.TerminationDate = '' then
    CurMethod.InfoPopup(messagetext = 
      'Temporary employees must have a termination' +
      ' date specified.', messagetype = MT_ERROR);
    errstat = -1;
  else
    CurObject.TerminationReason = 'Contract period end';
    errstat = CurMethod.SendSuperclass();
  endif;
  return errstat;
end   

/***************************************************************************
**  Method GetCurrent gets current job info for a temp employee.  Returns 0 
**  if succes, -1 if employee does not exist or is not temporary,
**  else error number.
***************************************************************************/

method GetCurrent() =
declare
  edate = date;
  errstat = integer not null;
  nrows = smallint not null;
enddeclare

begin
  select e.last_name as :CurObject.LastName, 
    e.first_name as :CurObject.firstName, 
    e.hire_date as :CurObject.HireDate, 
    e.term_date as :CurObject.TerminationDate, 
    e.term_reason as :CurObject.TerminationReason, 
    j.end_date as :edate,
    j.pay as :CurObject.CurrentPay, 
    j.job_class as :CurObject.CurrentClass, 
    c.job_title as :CurObject.CurrentTitle
  from v_emp e, v_jobs j, v_job_classes c
  where e.emp_num = :CurObject.EmpNum and e.emp_type = 't'
    and j.emp_num = e.emp_num and c.job_type = 't' 
    and c.job_class = j.job_class
  order by 6 desc
    begin
      endloop; /* get only the last job */
    end;
  errstat = CurMethod.DBSession.Errornumber;
  if errstat != 0 then;
    message 'Database error ' + varchar(errstat) 
      + '; cannot get employee data.';
  elseif CurMethod.DBSession.RowCount = 0 then
    errstat = -1;
    message 'No temp employee #' + varchar(CurObject.EmpNum);
  endif;
  return errstat;
end

/***************************************************************************
**  Method GetHistory gets complete job history for a temporary employee.
***************************************************************************/

method GetHistory() =
declare
  errstat = integer not null;
  nrows= smallint not null;
enddeclare

begin
  select last_name as :CurObject.LastName, 
    first_name as :CurObject.firstName, 
    hire_date as :CurObject.HireDate, 
    term_date as :CurObject.TerminationDate, 
    term_reason as :CurObject.TerminationReason
  from v_emp
  where emp_num = :CurObject.EmpNum and emp_type = 't';
  errstat = CurMethod.DBSession.Errornumber;
  if errstat != 0 then;
    message 'Database error ' + varchar(errstat) 
      + '; cannot get employee data.';
  elseif CurMethod.DBSession.RowCount = 0 then
    message 'No employee #' + varchar(CurObject.EmpNum);
    errstat = -1;
  else
    errstat = CurMethod.SendSuperclass();
  endif;
  return errstat;
end

initialize
(
	i = integer not null;
	v_no = integer not null;
	seq_no = integer not null;
	cv_date_1 = date;
	cv_date_2 = date;
	f_name = varchar(256) not null;
	sql_text = varchar(1024) not null;
	ii_sys_str = STRINGOBJECT;
	p_sum = STRINGOBJECT;
	b_map = BITMAPOBJECT;
) =

declare
	re_create_all = procedure;
	bmap_to_file = procedure;
       	strobj_to_file = procedure;
	drop_table = procedure;
	drop_view = procedure;
enddeclare

{
	if  ((curframe.dbsession.servertype != DS_SQLSERVER_DBMS) AND
	     (curframe.dbsession.servertype != DS_ORACLE_DBMS) AND
	     (curframe.dbsession.servertype != DS_INGRES_DBMS))
	then	curframe.ConfirmPopUp(messagetext =
			'Unknown DBMS connection type.  Abandoning.');
		return;
	endif;

	ii_sys_str.Value = CurSession.GetEnv(name = 'II_SYSTEM');
	/*
	** Only worry about PC and UNIX style file names.
	*/
	if  (ii_sys_str.LocateString(match = '/',startposition = 1) != 0)
	then	ii_sys_str.ConcatVarchar(text = '/ingres/files/w4gldemo/');
	else	ii_sys_str.ConcatVarchar(text = '\ingres\files\w4gldemo\');
	endif;

	dir_str = ii_sys_str.Value;
	file_str = 'vid';
	seq_no = 1;

	FIELD(status_display).CurBias = FB_INVISIBLE;
}

on click ok_btn =
{
	if  (rf_function_choice = 1)
	then	callproc re_create_all();
	elseif (rf_function_choice = 2)
	then	callproc bmap_to_file();
	elseif (rf_function_choice = 3)
	then	callproc strobj_to_file();
	else	curframe.ConfirmPopUp(messagetext =
			'Unknown function choice.  Abandoning.');
		return;
	endif;
}

on click cancel_btn =
{
	return;
}

procedure re_create_all() =
{
        if  (curframe.ConfirmPopUp(messagetext =
		'All data and tables will be destroyed.  Continue?') != PU_OK)
	then	return;
	endif;

	if  (dir_str = '')
	then	curframe.ConfirmPopUp(messagetext =
	'No directory string for graphic/plot summary files.  Abandoning');
		return;
	endif;

	if  (file_str = '')
	then	curframe.ConfirmPopUp(messagetext =
	'No file string for graphic/plot summary files.  Abandoning');
		return;
	endif;

	/*
	** COMMIT any outstanding transactions (errors, really),
	** and then set for AUTOCOMMIT ON.
	*/
	commit;
	curframe.dbsession.setautocommit(state = TRUE);

	FIELD(status_display).CurBias = FB_VISIBLE;
	CurFrame.Flush();

	status_display = 'Dropping v_checkout';
	CurFrame.Flush();
	callproc drop_table(table_name='v_checkout');

	status_display = 'Creating v_checkout';
	CurFrame.Flush();
	if  (curframe.dbsession.servertype = DS_SQLSERVER_DBMS)
	then	sql_text = 'create table v_checkout' + HC_NEWLINE +
			'(' + HC_NEWLINE +
			'acctno int,' + HC_NEWLINE +
			'vid_no int,' + HC_NEWLINE +
			'tape_no int,' + HC_NEWLINE +
			'date_out datetime null,' + HC_NEWLINE +
			'date_in datetime null,' + HC_NEWLINE +
			'price money null' + HC_NEWLINE +
			')';
