			'cname varchar(50) null,' + HC_NEWLINE +
			'caddr varchar(50) null,' + HC_NEWLINE +
			'ccity varchar(30) null,' + HC_NEWLINE +
			'cstate varchar(2) null,' + HC_NEWLINE +
			'czip varchar(5) null,' + HC_NEWLINE +
			'cphone int null,' + HC_NEWLINE +
			'cacctbal money null,' + HC_NEWLINE +
			'cstatus int null,' + HC_NEWLINE +
			'cdistrict varchar(20) null' + HC_NEWLINE +
			')';
	elseif (curframe.dbsession.servertype = DS_ORACLE_DBMS)
	then	sql_text = 'create table v_customer' + HC_NEWLINE +
			'(' + HC_NEWLINE +
			'acctno number(10) not null,' + HC_NEWLINE +
			'cname varchar2(50),' + HC_NEWLINE +
			'caddr varchar2(50),' + HC_NEWLINE +
			'ccity varchar2(30),' + HC_NEWLINE +
			'cstate varchar2(2),' + HC_NEWLINE +
			'czip varchar2(5),' + HC_NEWLINE +
			'cphone number(10),' + HC_NEWLINE +
			'cacctbal number(14,2),' + HC_NEWLINE +
			'cstatus number(10),' + HC_NEWLINE +
			'cdistrict varchar2(20)' + HC_NEWLINE +
			')';
	else	sql_text = 'create table v_customer' + HC_NEWLINE +
			'(' + HC_NEWLINE +
			'acctno int not null,' + HC_NEWLINE +
			'cname varchar(50),' + HC_NEWLINE +
			'caddr varchar(50),' + HC_NEWLINE +
			'ccity varchar(30),' + HC_NEWLINE +
			'cstate varchar(2),' + HC_NEWLINE +
			'czip varchar(5),' + HC_NEWLINE +
			'cphone int,' + HC_NEWLINE +
			'cacctbal money,' + HC_NEWLINE +
			'cstatus int,' + HC_NEWLINE +
			'cdistrict varchar(20)' + HC_NEWLINE +
			')';
	endif;

	execute immediate :sql_text;

