	status_display = 'Creating v_payment_quarterly';
	CurFrame.Flush();
	/*
	* SQLSERVER view has additional column PYEAR since the
	* DATE functions do not return a DATE, but an integer
	* (DATEPART) or a string (DATENAME).
	*/
	if  (curframe.dbsession.servertype = DS_SQLSERVER_DBMS)
	then	sql_text = 'create view v_payment_quarterly' + HC_NEWLINE +
			'(' + HC_NEWLINE +
			'acctno, pdate, pyear, pquarter, pamount' + HC_NEWLINE +
			') as' + HC_NEWLINE +
			'select acctno, pdate,' + HC_NEWLINE +
			'datepart(year,pdate),' + HC_NEWLINE +
			'datepart(quarter,pdate), pamount' + HC_NEWLINE +
			'from v_payment';
	elseif (curframe.dbsession.servertype = DS_ORACLE_DBMS)
	then	sql_text = 'create view v_payment_quarterly' + HC_NEWLINE +
			'(' + HC_NEWLINE +
			'acctno, pdate, pquarter, pamount' + HC_NEWLINE +
			') as' + HC_NEWLINE +
			'select acctno, pdate,' + HC_NEWLINE +
			'trunc(pdate,''Q''), pamount' + HC_NEWLINE +
			'from v_payment';
	else	sql_text = 'create view v_payment_quarterly' + HC_NEWLINE +
			'(' + HC_NEWLINE +
			'acctno, pdate, pquarter, pamount' + HC_NEWLINE +
			') as' + HC_NEWLINE +
			'select acctno, pdate,' + HC_NEWLINE +
			'date_trunc(''quarter'',pdate), pamount' + HC_NEWLINE +
			'from v_payment';
	endif;

	execute immediate :sql_text;

	sql_text = 'grant all on v_payment_quarterly to public';

	execute immediate :sql_text;

	status_display = 'Dropping v_plot_description';
	CurFrame.Flush();
	callproc drop_table(table_name = 'v_plot_description');

	status_display = 'Creating v_plot_description';
	CurFrame.Flush();
	if  (curframe.dbsession.servertype = DS_SQLSERVER_DBMS)
	then	sql_text = 'create table v_plot_description' + HC_NEWLINE +
			'(' + HC_NEWLINE +
			'string_id int,' + HC_NEWLINE +
			'row_sequence int,' + HC_NEWLINE +
			'text_total int,' + HC_NEWLINE +
			'text_value varchar(255)' + HC_NEWLINE +
			')';
	elseif (curframe.dbsession.servertype = DS_ORACLE_DBMS)
	then	sql_text = 'create table v_plot_description' + HC_NEWLINE +
			'(' + HC_NEWLINE +
			'string_id number(10) not null,' + HC_NEWLINE +
			'row_sequence number(10) not null,' + HC_NEWLINE +
			'text_total number(10) not null,' + HC_NEWLINE +
			'text_value varchar2(1786) not null' + HC_NEWLINE +
			')';
	else	sql_text = 'create table v_plot_description' + HC_NEWLINE +
			'(' + HC_NEWLINE +
			'string_id int not null,' + HC_NEWLINE +
