	status_display = 'Populating v_plot_description';
	CurFrame.Flush();
	i = 1;	
	while i <= seq_no
	do
		status_display = 'Populating v_plot_description (' + 
				varchar(i) + HC_SPACE + 'of' + HC_SPACE +
				varchar(seq_no) + ')';
		CurFrame.Flush();
		f_name = dir_str + file_str + varchar(i) + '.txt';
		p_sum.FileHandle = f_name;
		if  (p_sum.FileHandle != '')
		then
			if  (p_sum.InsertIntoDB(tablename =
				'v_plot_description') = ER_OK)
			then
				update v_video
					set plot_summ = :p_sum.DBHandle
					where vid_no = :i;
			endif;
		endif;
		i = i + 1;
	endwhile;

	status_display = 'Populating v_plot_description';
	CurFrame.Flush();
	i = 1;	
	while i <= seq_no
	do
		status_display = 'Populating v_video_graphics (' +
				varchar(i) + HC_SPACE + 'of' + HC_SPACE +
				varchar(seq_no) + ')';
