		if  (b_map.FileHandle != '')
		then
			if  (b_map.InsertIntoDB(tablename = 'v_video_graphics')
				= ER_OK)
			then
				update v_video
					set vid_graphic = :b_map.DBHandle
					where vid_no = :i;
			endif;
		endif;
		i = i + 1;
	endwhile;

	status_display = 'Dropping v_vendor';
	CurFrame.Flush();
	callproc drop_table(table_name = 'v_vendor');

	status_display = 'Creating v_vendor';
	CurFrame.Flush();
