	CurFrame.Flush();
	callproc drop_table(table_name = 'v_store');

	status_display = 'Creating v_store';
	CurFrame.Flush();
	if  (curframe.dbsession.servertype = DS_SQLSERVER_DBMS)
	then	sql_text = 'create table v_store' + HC_NEWLINE +
			'(' + HC_NEWLINE +
			'sname varchar(50) null,' + HC_NEWLINE +
			'saddr varchar(50) null,' + HC_NEWLINE +
