	** Make sure that we do this last!  This way, we're sure that all
	** required tables and/or views have been created and populated.
	*/
	status_display = 'Populating v_highest_number';
	CurFrame.Flush();

	select max(vid_no) as :v_no from v_video;
	insert into v_highest_number values
		(:v_no,'v_video');
	select max(emp_num) as :v_no from v_emp;
	insert into v_highest_number values
