	FIELD(status_display).CurBias = FB_INVISIBLE;
	CurFrame.Flush();

	curframe.dbsession.setautocommit(state = FALSE);

	return;
}

procedure strobj_to_file() =
{
        if  (curframe.ConfirmPopUp(messagetext =
	'Existing Plot Sumary files will be destroyed.  Continue?')
						!= PU_OK)
	then	return;
	endif;

	if  (dir_str = '')
	then	curframe.ConfirmPopUp(messagetext =
	'No directory string for plot summary files.  Abandoning');
		return;
	endif;

	if  (file_str = '')
	then	curframe.ConfirmPopUp(messagetext =
	'No file string for plot summary files.  Abandoning');
		return;
	endif;

	select	:p_sum.DBHandle = plot_summ,
		:v_no = vid_no
		from v_video
		order by v_no
	begin
		f_name = dir_str + file_str + varchar(v_no) + '.txt';
		p_sum.WritetoFile(filename = f_name);
	end;

	commit;
}

procedure drop_table
(
	table_name = varchar(256) not null;
) =
declare
	table_exist = integer not null;
	uid_ss      = integer not null;
	uppercase_table_name = varchar(256) not null;
enddeclare
{
	table_exist=0;
	if  (curframe.dbsession.servertype = DS_SQLSERVER_DBMS)
	then	
		select user_id() as uid_ss;
		select count(*) as table_exist from sysobjects
		 where name=:table_name and type='U' and uid=:uid_ss;
	elseif (curframe.dbsession.servertype = DS_ORACLE_DBMS)
	then	
		uppercase_table_name = uppercase(table_name);
		select count(*) as table_exist from user_tables 
		 where table_name=:uppercase_table_name;
	else	
		table_exist=1;
	endif;

	if  (table_exist=1)
	then	drop table :table_name;
	endif;
	commit;
}

procedure drop_view
(
	view_name = varchar(256) not null;
) =
declare
	view_exist = integer not null;
	uid_ss      = integer not null;
	uppercase_view_name = varchar(256) not null;
enddeclare
{
	view_exist=0;
	if  (curframe.dbsession.servertype = DS_SQLSERVER_DBMS)
	then	
		select user_id() as uid_ss;
		select count(*) as view_exist from sysobjects
		 where name=:view_name and type='V' and uid=:uid_ss;
	elseif (curframe.dbsession.servertype = DS_ORACLE_DBMS)
	then	
		uppercase_view_name = uppercase(view_name);
		select count(*) as view_exist from user_views 
		 where view_name=:uppercase_view_name;
	else	
		view_exist=1;
	endif;

	if  (view_exist=1)
	then	drop view :view_name;
	endif;
	commit;
}

procedure bmap_to_file() =
{
        if  (curframe.ConfirmPopUp(messagetext =
	'Existing Video Bitmap files will be destroyed.  Continue?')
						!= PU_OK)
	then	return;
	endif;

	if  (dir_str = '')
	then	curframe.ConfirmPopUp(messagetext =
	'No directory string for bitmap files.  Abandoning');
		return;
	endif;

	if  (file_str = '')
	then	curframe.ConfirmPopUp(messagetext =
	'No file string for bitmap files.  Abandoning');
		return;
	endif;

	select	:b_map.DBHandle = vid_graphic,
		:v_no = vid_no
		from v_video
		order by v_no
	begin
		f_name = dir_str + file_str + varchar(v_no) + '.gif';
		b_map.WritetoFile(filename = f_name,format = BF_GIF);
	end;

	commit;
}

