COMPONENT.script:
/*
** Name: video_detail - Displays details of a single video.
**
** Description:
**	This displays the details of a single video.
**	If read_only is FALSE, you can actually insert, update, or delete
**	a video in this frame. If read_only is TRUE, you can only view it.
**
** Called By:
**	video_list, via openframe.
**
** Parameters:
**	video_info	Row of video list table field in video_list frame.
**	read_only	Only view details of video.
**
** Returns:
**	(None)
**
** User Events:
**	'UpdateGraphic' sent to video_graphic when graphic is changed.
**	'UpdateTitle' sent to video_graphic when title is changed.
**	'GraphicClosed' sent from video_graphic when frame is closed.
**
**	'InsertEntry' sent to video_list when a video is inserted.
**	'UpdateEntry' sent to video_list when a video is updated.
**	'DeleteEntry' sent to video_list when a video is deleted.
**	'CloseDetail' sent to video_list when this frame is closed.
**
**	'Cleanup' sent from video_list when it wants this frame to check if
**		changes have been made and deal with them.
**	'Done' sent to video_list when this frame is done dealing
**		with the changes.
*/

initialize
(
   video_info = VIDEO_ROW;		/* Row of table field which
					** contains all info for video.
					*/
   read_only = integer not null;	/* Only view details of video. */
) =
declare
   new_video = VIDEO_ROW,		/* Video sent to video_list frame for
					** 'InsertEntry' and 'UpdateEntry'
					** user events.
					*/

   parent_frame = FrameExec,		/* Parent frame (video_list frame) */
   status = integer not null,		/* Reply from confirmation */
   file_name = StringObject,		/* File name for changing plot or
					** graphic */
   db_timestamp = date,			/* Time stamp of video in database */
   cleaning_up = integer not null,	/* Flag to determine if cleaning up */
   graphic_frame = FrameExec,		/* Video graphic frame. */
   verb = varchar(10) not null
enddeclare
begin
    cleaning_up = FALSE;

    /* Do not allow changes if only viewing details */
    if read_only = TRUE then
      CurFrame.CurMode = FM_READ;
    else
      if video_info.vid_no = 0 then
        /* Create New was done. */
        field(update_button).CurBias = FB_DIMMED;
        field(delete_button).CurBias = FB_DIMMED;
      else
        field(insert_button).CurBias = FB_DIMMED;
      endif;
    endif;

    parent_frame = FrameExec(CurFrame.ParentFrame);

    /* Load the video. We duplicate so editing in this frame does not affect
    ** the table field in video_list until an update is done. */
    video_info.details_frame = null;	/* Don't duplicate the frame exec */
    video = video_info.Duplicate();
    video_info.details_frame = CurFrame;	/* Set it back */

    /* Load plot summary */
    if video.plot_summ != '' then
      video.plot_summ_stringobject.DBHandle = video.plot_summ;
      if video.plot_summ_stringobject.ErrorStatus != ER_OK then
        message 'Cannot load the plot summary from the database.';
      endif;
    endif;

    /* Load graphic */
    if video.vid_graphic != '' then
      video.vid_graphic_bitmapobject.DBHandle = video.vid_graphic;
      if video.vid_graphic_bitmapobject.ErrorStatus != ER_OK then
        message 'Cannot load the graphic from the database.';
      endif;
    endif;

    /* Show video graphic if it was set previously. */
    menu.view_menu.show_graphic = Show_video_graphic;
    if Show_video_graphic = TRUE then
      CurFrame.WindowVisibility = WV_VISIBLE;
      graphic_frame = openframe video_graphic(vid_graphic_bitmap =
	video.vid_graphic_bitmapobject)
	with windowtitle = video.title,
	windowxleft = CurFrame.WindowWidth,
	windowytop = 0;
    else
      graphic_frame = null;
    endif;
end

on setvalue video.title =
begin
    CurFrame.WindowTitle = video.title;
    if graphic_frame is not null then
      graphic_frame.SendUserEvent(eventname = 'UpdateTitle',
				  messagevarchar = video.title);
    endif;
end

on click menu.view_menu.show_graphic =
begin
    if menu.view_menu.show_graphic = FALSE then
      graphic_frame.Terminate();
      graphic_frame = null;
    else
      graphic_frame = openframe video_graphic(vid_graphic_bitmap =
	video.vid_graphic_bitmapobject)
	with windowtitle = video.title,
	windowxleft = CurFrame.WindowWidth,
	windowytop = 0;
    endif;

    Show_video_graphic = menu.view_menu.show_graphic;
end

on userevent 'GraphicClosed' =
begin
    menu.view_menu.show_graphic = FALSE;
    Show_video_graphic = menu.view_menu.show_graphic;
    graphic_frame = null;
end

on click menu.file_menu.read_plot_summ_from_file =
begin
    /* Prompt the user for a file name. */
    file_name.Value = '';
    status = CurFrame.FilePopup(messagetext =
      'Enter name of file containing plot summary:',
      reply = file_name);
    if status = PU_OK then
      /* Now actually read the file into the entry field. */
      video.plot_summ_stringobject.FileHandle = file_name.Value;
      if video.plot_summ_stringobject.ErrorStatus != ER_OK then
        message 'Cannot read the plot summary from given file.';
      else
	CurFrame.TopForm.HasDataChanged = TRUE;
      endif;
    endif;
end

on click menu.file_menu.read_vid_graphic_from_file =
begin
    /* Prompt the user for a file name. */
    file_name.Value = '';
    status = CurFrame.FilePopup(messagetext =
				'Enter name of file containing graphic:',
				reply = file_name);
    if status = PU_OK then
      /* Now actually read the file into the image trim. */
      video.vid_graphic_bitmapobject.FileHandle = file_name.Value;
      if video.vid_graphic_bitmapobject.ErrorStatus != ER_OK then
        message 'Cannot read the graphic from given file.';
      else
	CurFrame.TopForm.HasDataChanged = TRUE;
	if graphic_frame is not null then
	  graphic_frame.SendUserEvent(eventname = 'UpdateGraphic',
				      messageobject =
						video.vid_graphic_bitmapobject);
	endif;
      endif;
    endif;
end

on click insert_button =
begin
    /* Generate a new video number */
    update v_highest_number
    set h_number = h_number + 1
    where tbl_name = 'v_video';

    select h_number as :video.vid_no
    from v_highest_number
    where tbl_name = 'v_video';

    commit;

    /* Insert plot summary into database */
    status = video.plot_summ_stringobject.InsertIntoDB(tablename =
						       'v_plot_description');
    if status = ER_OK then
      video.plot_summ = video.plot_summ_stringobject.DBHandle;
    else
      message 'Cannot insert plot summary into the database. ' +
	      dbms_error_message();
      rollback;
      resume;
    endif;

    /* Insert graphic into database */
    status = 
    video.vid_graphic_bitmapobject.InsertIntoDB(tablename =
      'v_video_graphics');
    if status = ER_OK then
      video.vid_graphic = video.vid_graphic_bitmapobject.DBHandle;
    else
      message 'Cannot insert graphic into the database. ' +
	      dbms_error_message();
      rollback;
      resume;
    endif;

    video.timestamp = date('now');

    /* Actually insert video into the database */
    insert into v_video (vid_no,
			 vacctno,
			 title,
			 plot_summ,
			 vid_graphic,
			 director,
			 category,
			 length,
			 year,
			 color,
			 country,
			 subtitle,
			 language,
			 foreign_title,
			 last_checkout,
			 no_checkouts,
			 timestamp)
    values (video.vid_no,
	    video.vacctno,
	    video.title,
	    video.plot_summ,
	    video.vid_graphic,
	    video.director,
	    video.category,
	    video.length,
	    video.year,
	    video.color,
	    video.country,
	    video.subtitle,
	    video.language,
	    video.foreign_title,
	    '',
	    0,
	    video.timestamp);

    if iierrornumber = 0 then
      commit;

      CurFrame.TopForm.HasDataChanged = FALSE;

      field(insert_button).CurBias = FB_DIMMED;
      field(update_button).CurBias = FB_CHANGEABLE;
      field(delete_button).CurBias = FB_CHANGEABLE;

      /* Tell video_list to insert video in list */
      new_video = video.Duplicate();
      new_video.details_frame = CurFrame;
      parent_frame.SendUserEvent(eventname = 'InsertEntry',
	messageobject = new_video);
    else
      message 'Cannot insert video into the database. ' + dbms_error_message();
      rollback;
    endif;

    if cleaning_up = TRUE then
      parent_frame.SendUserEvent(eventname = 'Done');
      return;
    endif;
end

on click update_button =
begin
    /* Check if video has been updated by someone else since we've loaded it */
    select :db_timestamp = timestamp
    from v_video
    where vid_no = :video.vid_no;
    commit;

    if db_timestamp > video.timestamp then
      message 'Cannot update since someone else has already updated this video.';
      resume;
    endif;

    if video.plot_summ_stringobject.DBHandle = '' then
      /* Insert plot summary into database */
      status = video.plot_summ_stringobject.InsertIntoDB(tablename =
						         'v_plot_description');
      if status = ER_OK then
        video.plot_summ = video.plot_summ_stringobject.DBHandle;
      else
        message 'Cannot insert plot summary into the database. ' +
	        dbms_error_message();
        rollback;
        resume;
      endif;
    else
      /* Update plot summary in database */
      status = video.plot_summ_stringobject.UpdateInDB();
      if status != ER_OK then
        message 'Cannot update plot summary in the database. ' +
		dbms_error_message();
        rollback;
        resume;
      endif;
    endif;

    if video.vid_graphic_bitmapobject.DBHandle = '' then
      /* Insert graphic into database */
      status = video.vid_graphic_bitmapobject.InsertIntoDB(tablename =
							   'v_video_graphics');
      if status = ER_OK then
        video.vid_graphic = video.vid_graphic_bitmapobject.DBHandle;
      else
        message 'Cannot insert graphic into the database. ' +
	        dbms_error_message();
        rollback;
        resume;
      endif;
    else
      /* Update graphic in database */
      status = video.vid_graphic_bitmapobject.UpdateInDB();
      if status != ER_OK then
        message 'Cannot update graphic in the database. ' +
		dbms_error_message();
        rollback;
        resume;
      endif;
    endif;

    video.timestamp = date('now');

    /* Actually update video in the database */
    update v_video
    set vacctno = :video.vacctno,
	title = :video.title,
	plot_summ = :video.plot_summ,
	vid_graphic = :video.vid_graphic,
	director = :video.director,
	category = :video.category,
	length = :video.length,
	year = :video.year,
	color = :video.color,
	country = :video.country,
	subtitle = :video.subtitle,
	language = :video.language,
	foreign_title = :video.foreign_title,
	timestamp = :video.timestamp
    where vid_no = :video.vid_no;

    if iierrornumber = 0 then
      commit;

      CurFrame.TopForm.HasDataChanged = FALSE;

      /* Tell video_list to update video in list */
      new_video = video.Duplicate();
      new_video.details_frame = CurFrame;
      parent_frame.SendUserEvent(eventname = 'UpdateEntry',
	messageobject = new_video);
    else
      message 'Cannot update video in the database. ' + dbms_error_message();
      rollback;
    endif;

    if cleaning_up = TRUE then
      parent_frame.SendUserEvent(eventname = 'Done');
      return;
    endif;
end

on click delete_button =
begin
    status = CurFrame.ConfirmPopup(messagetext =
     'You are going to delete this video.');
    if status != PU_OK then
      resume;
    endif;

    /* Delete plot summary from database */
    if video.plot_summ_stringobject.DBHandle != '' then
      status = video.plot_summ_stringobject.DeleteFromDB();
      if status != ER_OK then
        message 'Cannot delete plot summary from the database. ' +
		dbms_error_message();
        rollback;
        resume;
      endif;
    endif;

    /* Delete graphic from database */
    if video.vid_graphic_bitmapobject.DBHandle != '' then
      status = video.vid_graphic_bitmapobject.DeleteFromDB();
      if status != ER_OK then
        message 'Cannot delete graphic from the database. ' +
		dbms_error_message();
        rollback;
        resume;
      endif;
    endif;

    /* Actually delete video from the database */
    delete from v_video where vid_no = :video.vid_no;
    if iierrornumber = 0 then
      commit;

      field(insert_button).CurBias = FB_CHANGEABLE;

      /* Prevent further deletes or updates */
      field(update_button).CurBias = FB_DIMMED;
      field(delete_button).CurBias = FB_DIMMED;

      /* Clear all fields on form */
      CurFrame.TopForm.SetToDefault();

      /* Tell graphic to update itself */
      if graphic_frame is not null then
        graphic_frame.SendUserEvent(eventname = 'UpdateGraphic',
	  messageobject = video.vid_graphic_bitmapobject);
      endif;

      /* Tell video_list to delete video from list */
      parent_frame.SendUserEvent(eventname = 'DeleteEntry',
	messageobject = CurFrame);
    else
      message 'Cannot delete video from the database. ' + dbms_error_message();
      rollback;
    endif;
end

on click close_button,
on windowclose =
begin
    if cleaning_up = FALSE then
      if CurFrame.TopForm.HasDataChanged = TRUE then
        status = CurFrame.ConfirmPopup(messagetext =
			  'Some of the fields on the form have been changed. ' +
			  'Closing the frame will lose these changes.');
      else
        status = PU_OK;
      endif;

      if status = PU_OK then
        parent_frame.SendUserEvent(eventname = 'CloseDetail',
	  messageobject = CurFrame);
        return;
      endif;
    else
      parent_frame.SendUserEvent(eventname = 'Done');
      return;
    endif;
end

on userevent 'Cleanup' =
begin
    if CurFrame.TopForm.HasDataChanged = TRUE then
      CurFrame.WindowVisibility = WV_VISIBLE;	/* In case it was iconized */
      CurFrame.BringToFront();	/* In case it was behind some other window */
      field(delete_button).CurBias = FB_DIMMED;

      if video.vid_no = 0 then
	verb = 'Insert';
      else
	verb = 'Update';
      endif;

      CurFrame.InfoPopup(messagetext =
			'Some of the fields on the form have been changed. ' +
			verb + ' the video with the changes or ' +
			'close the frame, discarding the changes.');

      cleaning_up = TRUE;
    else
      parent_frame.SendUserEvent(eventname = 'Done');
    endif;
end

on windowicon =
begin
    if graphic_frame is not null then
      graphic_frame.WindowVisibility = WV_ICON;
    endif;
end

row.script:
ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	IF table_field.ActiveRow = 0 THEN
		MESSAGE 'You must select a row of the table field ' +
			'to insert a row before it.';
		RESUME;
	ENDIF;
	tf_array.InsertRow(rownumber = table_field.ActiveRow);
	table_field.HasDataChanged = TRUE;
	table_field.ActiveRow = table_field.CurRow - 1;
	tf_array[table_field.ActiveRow]._Rowstate = RS_UNDEFINED;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	IF table_field.ActiveRow = 0 THEN
		MESSAGE 'You must select a row of the table field ' +
			'to delete it.';
		RESUME;
	ENDIF;
	tf_array.SetRowDeleted(rownumber = table_field.ActiveRow);
	table_field.HasDataChanged = TRUE;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	tf_array.Clear();
	table_field.HasDataChanged = TRUE;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	IF table_field.ActiveRow = 0 THEN
		MESSAGE 'You must select a row of the table field ' +
			'to insert a row before it.';
		RESUME;
	ENDIF;
	tf_array.InsertRow(rownumber = table_field.ActiveRow);
	table_field.HasDataChanged = TRUE;
	table_field.ActiveRow = table_field.CurRow - 1;
	tf_array[table_field.ActiveRow]._Rowstate = RS_UNDEFINED;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	IF table_field.ActiveRow = 0 THEN
		MESSAGE 'You must select a row of the table field ' +
			'to delete it.';
		RESUME;
	ENDIF;
	tf_array.SetRowDeleted(rownumber = table_field.ActiveRow);
	table_field.HasDataChanged = TRUE;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	tf_array.Clear();
	table_field.HasDataChanged = TRUE;
END

COMPONENT.script:
/*
** Name: video_list	- Displays the list of videos in the store.
**
** Description:
**	This displays the list of videos in the store, ordered by title.
**	You can view the details of selected videos at the same time.
**	If you are the DBA, you can edit the details of a selected video.
**
** Called By:
**	main_control, via callframe.
**
** Parameters:
**	(None)
**
** Returns:
**	(None)
**
** User Events:
**	'DeleteEntry' sent from video_detail when a video is deleted.
**	'UpdateEntry' sent from video_detail when a video is updated.
**	'InsertEntry' sent from video_detail when a video is inserted.
**	'CloseDetail' sent from video_detail when it is closed.
**
**	'Cleanup' sent to video_detail when we want it to confirm if changes
**		were made and do something with them.
**	'Done' sent from video_detail when changes are done.
**
*/

initialize() =
declare
  user_name = varchar(32) not null;	/* user name */
  dba_name = varchar(32) not null;	/* DBA name */
  i = integer not null;
  video = VIDEO_ROW;			/* Video passed from video_detail frame
					** for 'UpdateEntry' and 'InsertEntry'
					** user events.  */
  close_details_frame = FrameExec;	/* video_detail frame pointer passed
					** from video_detail frame for
					** 'CloseDetail' user event.  */
  delete_details_frame = FrameExec;	/* video_detail frame pointer passed
					** from video_detail frame for
					** 'DeleteEntry' user event.  */
  new_details_frames = array of FrameExec;
					/* Keeps track of all detail frames
					** used for creating new videos. */
  sort_order = integer not null;                  
  find_text = varchar(100) not null;
  find_match = integer not null default 1;
  find_ignore_case = integer not null default 1;
  find_column_name = varchar(32) not null;
  find_column_title = varchar(32) not null;
  sort_column = smallint not null;
enddeclare

begin

  if  (curframe.dbsession.servertype = DS_SQLSERVER_DBMS)
  then	select user_name() as :user_name;
  	commit;
	dba_name = 'dbo';
  elseif (curframe.dbsession.servertype = DS_ORACLE_DBMS)
  then	select username as :user_name from user_users;
  	commit;
	dba_name = 'system';
  elseif (curframe.dbsession.servertype = DS_INGRESDSK_DBMS)
  then	user_name = '';
  	dba_name = '';
  elseif (curframe.dbsession.servertype = DS_INGRES_DBMS)
  then	
	/*
	** Use two selects, because gateways don't allow
	** more than one dbmsinfo() call per query.
	*/
	select dbmsinfo('username') as :user_name;
	select dbmsinfo('dba') as :dba_name;
  	commit;
  else	user_name = '';
  	dba_name = '';
  endif;

  /* 
  ** Make edit and create buttons invisible if the user is not the DBA.
  ** (You could use a table of authorized users instead of just the DBA.) 
  ** Note that SQLServer reflects "super" user names as 'dbo' and not
  ** the actual/-u user name since you can't connect as 'dbo'.  Thus,
  ** for these users, user_name will always equal dba_name.
  */

  if (lowercase(user_name) != lowercase(dba_name))
  then
    field(edit_button).CurBias = FB_INVISIBLE;
    field(create_button).CurBias = FB_INVISIBLE;
  endif; 

  /* Load the table field with all the videos */
  i = 1;
  select vid_no as :vlist[i].vid_no,
  	 vacctno as :vlist[i].vacctno,
	 title as :vlist[i].title,
	 plot_summ as :vlist[i].plot_summ,
	 vid_graphic as :vlist[i].vid_graphic,
	 director as :vlist[i].director,
	 category as :vlist[i].category,
	 length as:vlist[i].length ,
	 year as :vlist[i].year,
	 color as :vlist[i].color,
	 country as :vlist[i].country,
	 subtitle as :vlist[i].subtitle,
	 language as :vlist[i].language,
	 foreign_title as :vlist[i].foreign_title,
	 last_checkout as :vlist[i].last_checkout,
	 no_checkouts as :vlist[i].no_checkouts,
	 timestamp as :vlist[i].timestamp
  from v_video
  order by title
    begin
      i = i + 1;
    end;
  commit;
  sort_order = AS_ASC;  /* make ascending the default sort order */
  sort_column = 1;	/* make "title" the default sort column */
end

on click vlist_cb.sortmenu.mlist_tcn =
begin
  sort_column = field(vlist_cb.sortmenu.mlist_tcn).curenumvalue;
  if sort_column = 1 then
    vlist.sort(title = sort_order);
  elseif sort_column = 2 then
    vlist.sort(category = sort_order);
  elseif sort_column = 3 then
    vlist.sort(vid_no = sort_order);
  endif;
end

on click vlist_cb.sortmenu.mlist_asc_desc =
begin
  if field(vlist_cb.sortmenu.mlist_asc_desc).curenumvalue = 1 then
    sort_order = AS_ASC;
  else 
    sort_order = AS_DESC;
  endif;

  if sort_column = 1 then
    vlist.sort(title = sort_order);
  elseif sort_column = 2 then
    vlist.sort(category = sort_order);
  elseif sort_column = 3 then
    vlist.sort(vid_no = sort_order);
  endif;
end

on click vlist_cb.find_submenu.title_button = 
begin
  i = callframe find_string(title = 'VideoList Search',
    array_name = vlist,
    search_string = byref(find_text),
    match = byref(find_match), 
    ignore_case = byref(find_ignore_case), 
    starting_row = field(vlist).CurRow + 1,
    column_name = 'title',
    column_title = 'Title');
  if i > 0 then
    field(vlist).setinputfocus(row = i);
  endif;
end

on click vlist_cb.find_submenu.category_button = 
begin
  i = callframe find_string(title = 'VideoList Search',
    array_name = vlist,
    search_string = byref(find_text),
    match = byref(find_match),
    ignore_case = byref(find_ignore_case),
    starting_row = field(vlist).CurRow + 1,
    column_name = 'category',
    column_title = 'Category');
  if i > 0 then
    field(vlist).setinputfocus(row = i);
  endif;
end

on click vlist_cb.find_submenu.number_button = 
begin
  i = callframe find_string(title = 'VideoList Search',
    array_name = vlist,
    search_string = byref(find_text),
    match = byref(find_match),
    ignore_case = byref(find_ignore_case),
    starting_row = field(vlist).CurRow + 1,
    column_name = 'vid_no',
    column_title = 'Number');
  if i > 0 then
    field(vlist).setinputfocus(row = i);
  endif;
end

on click edit_button =
begin
  if vlist[].details_frame is null then
    vlist[].details_frame = openframe video_detail(video_info = vlist[],
       read_only = FALSE) with windowtitle = vlist[].title;
    if user_name = dba_name then
      field(edit_button).CurBias = FB_DIMMED;
    endif;
  endif;
end

on click view_button =
begin
  if vlist[].details_frame is null then
    vlist[].details_frame = openframe video_detail(video_info = vlist[],
      read_only = TRUE) 
      with windowtitle = vlist[].title;
    if user_name = dba_name then
      field(edit_button).CurBias = FB_DIMMED;
    endif;
  endif;
end

on childentry vlist =
begin
  if user_name = dba_name then
    if vlist[].details_frame is null then
      field(edit_button).CurBias = FB_LANDABLE;
    else
      field(edit_button).CurBias = FB_DIMMED;
    endif;
  endif;
end

on userevent 'DeleteEntry' =
begin
  delete_details_frame = FrameExec(CurFrame.MessageObject); 

  /* Determine which row in table contains video. */
  if vlist.Find(AttributeName = 'details_frame',
    Value = delete_details_frame, RowNumber = byref(i)) = ER_OK then
    /* Delete it from table field */
    vlist.RemoveRow(rownumber = i);
  endif;

  /* Add it to new details frames list, since it is now in insert mode */
  i = new_details_frames.LastRow + 1;
  new_details_frames[i] = delete_details_frame;
end

on userevent 'UpdateEntry' =
begin
  video = VIDEO_ROW(CurFrame.MessageObject);
  /* Determine which row in table contains video. */
  /* 
  ** Modify Value parameter to video.details_frame in order to
  ** reflect changes in video_detail frame to video_list frame.
  */
  if vlist.Find(AttributeName = 'details_frame',
  Value = video.details_frame, RowNumber = byref(i)) = ER_OK then
    /* Update entry in table. */
    vlist[i] = video;
  endif;
end

on userevent 'InsertEntry' =
begin
  /* Insert new row in video list right in front of the current row, and
  ** move cursor there */
  video = VIDEO_ROW(CurFrame.MessageObject);
  i = field(vlist).CurRow;
  vlist.InsertRow(rownumber = i, rowobject = video);
  field(vlist).ActiveRow = i;

  /* Remove it from new details frames list,
  ** since it is not in insert mode anymore */
  callproc remove_new_detail(new_details_frames = new_details_frames,
    details_frame = video.details_frame);
end

on userevent 'CloseDetail' =
begin
  close_details_frame = FrameExec(CurFrame.MessageObject);
  if vlist[].details_frame = close_details_frame then
    /* Row currently selected is associated with closed details frame, 
    ** allow it to be viewed or edited again. */
    vlist[].details_frame = null;
    if user_name = dba_name then
      field(edit_button).CurBias = FB_LANDABLE;
    endif;
  elseif vlist.Find(AttributeName = 'details_frame', 
    Value = close_details_frame, RowNumber = byref(i),
    StartRow = 1) = ER_OK then
    /* We found which row in table contains video; set its details frame
    ** pointer to null so it can be edited again. */
    vlist[i].details_frame = null;
  else
    /* It was details for creating a new video so remove it from list */
    callproc remove_new_detail(new_details_frames = new_details_frames,
      details_frame = close_details_frame);
  endif;
end

on click create_button =
begin
  video = VIDEO_ROW.Create();
  i = new_details_frames.LastRow + 1;
  new_details_frames[i] = openframe video_detail(video_info = video,
    read_only = FALSE) with windowtitle = 'Video with no name';
end

on click close_button,
on windowclose =
begin
  /* If there are any detail frames open, tell them to cleanup themselves. */
  for i = 1 to vlist.LastRow do
    if vlist[i].details_frame is not null then
      vlist[i].details_frame.SendUserEvent(eventname = 'Cleanup',
	focusbehavior = FT_SETVALUE);
      CurFrame.WaitFor(eventname = 'Done');
    endif;
  endfor;

  /* If there are any detail frames for new videos, tell them to cleanup. */
  for i = 1 to new_details_frames.LastRow do
    new_details_frames[i].SendUserEvent(eventname = 'Cleanup',
      focusbehavior = FT_SETVALUE);
    CurFrame.WaitFor(eventname = 'Done');
  endfor;

  return;
end

row.script:
ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	IF table_field.ActiveRow = 0 THEN
		MESSAGE 'You must select a row of the table field ' +
			'to insert a row before it.';
		RESUME;
	ENDIF;
	tf_array.InsertRow(rownumber = table_field.ActiveRow);
	table_field.HasDataChanged = TRUE;
	table_field.ActiveRow = table_field.CurRow - 1;
	tf_array[table_field.ActiveRow]._Rowstate = RS_UNDEFINED;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	IF table_field.ActiveRow = 0 THEN
		MESSAGE 'You must select a row of the table field ' +
			'to delete it.';
		RESUME;
	ENDIF;
	tf_array.SetRowDeleted(rownumber = table_field.ActiveRow);
	table_field.HasDataChanged = TRUE;
END

ON CLICK =
DECLARE
	table_field = TABLEFIELD;
	tf_array = ARRAY OF OBJECT;
ENDDECLARE
BEGIN
	table_field = CurFrame.TriggerField.WhichTableField();
	table_field.GetFieldValue(value = byref(tf_array));
	tf_array.Clear();
	table_field.HasDataChanged = TRUE;
END
